import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [role, setRole] = useState('cadre'); // Default to 'cadre'
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadText, setUploadText] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileType, setFileType] = useState('pdf'); // Default to PDF
  const [imageType, setImageType] = useState('ocr');
  const [startPage, setStartPage] = useState(1);
  const [conversations, setConversations] = useState({});
  const [currentThread, setCurrentThread] = useState(null);
  const [showSidebar, setShowSidebar] = useState(false); // For sidebar toggle
  const [showUploadModal, setShowUploadModal] = useState(false); // For upload modal

  const API_BASE = 'http://localhost:5000';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchConversations();
      setIsAuthenticated(true);
    }
  }, []);

  const fetchConversations = async () => {
    try {
      const res = await axios.get(`${API_BASE}/conversations`);
      console.log('Fetched conversations:', res.data);
      setConversations(res.data);
    } catch (error) {
      console.error('Error fetching conversations:', error);
    }
  };

  const handleRegister = async () => {
    try {
      const res = await axios.post(`${API_BASE}/register`, {
        first_name: firstName,
        role,
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setShowRegister(false);
      setFirstName('');
      setRole('cadre');
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await axios.post(`${API_BASE}/login`, {
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setIsAuthenticated(false);
    setUser(null);
    setConversations({});
    setCurrentThread(null);
  };

  const handleUpload = async () => {
    if (fileType === 'text' && !uploadText.trim()) return alert('Text required');
    if (fileType !== 'text' && !selectedFile) return alert('File required');

    setUploading(true);
    try {
      if (fileType === 'text') {
        await axios.post(`${API_BASE}/upload-text`, { text: uploadText });
        alert('Text uploaded');
        setUploadText('');
      } else {
        const formData = new FormData();
        formData.append('file', selectedFile);
        if (fileType === 'pdf') formData.append('startPage', startPage);
        if (fileType === 'image') formData.append('imageType', imageType);
        await axios.post(`${API_BASE}/upload-file`, formData, {
          headers: { 'Content-Type': 'multipart/form-data' }
        });
        alert(`${fileType.toUpperCase()} uploaded`);
      }
      setSelectedFile(null);
      setStartPage(1);
      setImageType('ocr');
      setShowUploadModal(false);
    } catch (error) {
      alert('Upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
    }
  };

  const handleAsk = async () => {
    if (!question.trim()) return;
    setLoading(true);
    try {
      const res = await axios.post(`${API_BASE}/ask`, {
        question,
        thread_id: currentThread
      });
      setAnswer(res.data.answer);
      setCurrentThread(res.data.thread_id);
      fetchConversations();
    } catch (error) {
      setAnswer('Error: Failed to get answer - ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setLoading(false);
      setQuestion('');
    }
  };

  const startNewConversation = () => {
    setCurrentThread(null);
    setAnswer('');
  };

  const getAcceptedTypes = () => {
    switch (fileType) {
      case 'pdf': return '.pdf';
      case 'image': return 'image/*';
      case 'csv': return '.csv';
      case 'excel': return '.xlsx,.xls';
      default: return '';
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex">
      {!isAuthenticated ? (
        <div className="bg-white p-8 rounded shadow-md w-96 mx-auto my-auto">
          <h2 className="text-2xl font-bold mb-4">{showRegister ? 'Register' : 'Login'}</h2>
          {showRegister && (
            <>
              <input
                type="text"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                placeholder="First Name"
                className="w-full p-2 mb-4 border rounded"
              />
              <select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                className="w-full p-2 mb-4 border rounded"
              >
                <option value="cadre">cadre</option>
                <option value="detector">detector</option>
                <option value="Chef de division">Chef de division</option>
                <option value="Chef de service">Chef de service</option>
                <option value="administrator">administrator</option>
              </select>
            </>
          )}
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
            className="w-full p-2 mb-4 border rounded"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            className="w-full p-2 mb-4 border rounded"
          />
          <button
            onClick={showRegister ? handleRegister : handleLogin}
            className="w-full bg-blue-500 text-white p-2 rounded"
          >
            {showRegister ? 'Register' : 'Login'}
          </button>
          <p className="mt-4 text-center">
            {showRegister ? 'Have an account?' : 'Need an account?'} <span onClick={() => setShowRegister(!showRegister)} className="text-blue-500 cursor-pointer">{showRegister ? 'Login' : 'Register'}</span>
          </p>
        </div>
      ) : (
        <>
          {/* Sidebar for Conversation History */}
          <div className={`fixed inset-y-0 left-0 transform ${showSidebar ? 'translate-x-0' : '-translate-x-full'} transition duration-200 ease-in-out z-30 w-64 bg-white shadow-lg overflow-y-auto`}>
            <div className="p-4">
              <h3 className="text-lg font-semibold mb-4">Conversation History</h3>
              {Object.keys(conversations).length === 0 ? (
                <p>No conversations yet.</p>
              ) : (
                Object.keys(conversations).map((thread) => (
                  <button
                    key={thread}
                    onClick={() => { setCurrentThread(thread); setShowSidebar(false); }}
                    className="block w-full text-left p-2 border-b"
                  >
                    {conversations[thread][0]?.question || thread.slice(0, 8)}
                  </button>
                ))
              )}
              <button onClick={() => { startNewConversation(); setShowSidebar(false); }} className="mt-4 bg-green-500 text-white px-4 py-2 rounded w-full">New Conversation</button>
            </div>
          </div>

          {/* Main Content */}
          <div className="flex-1 p-4">
            <div className="flex justify-between items-center mb-4">
              <div>
                <h1 className="text-2xl font-bold">HCP Chatbot</h1>
                <p className="text-lg">Welcome, {user.first_name}</p>
              </div>
              <div className="flex items-center">
                <button onClick={() => setShowSidebar(!showSidebar)} className="mr-4 bg-blue-500 text-white px-4 py-2 rounded">History</button>
                <button onClick={() => setShowUploadModal(true)} className="mr-4 bg-green-500 text-white px-4 py-2 rounded">Upload Document</button>
                <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded">Logout</button>
              </div>
            </div>

            <div className="bg-white p-6 rounded-lg shadow">
              <h3 className="text-lg font-semibold mb-4">Ask Questions</h3>
              {currentThread && conversations[currentThread] && (
                <div className="mb-4 max-h-64 overflow-y-auto">
                  {conversations[currentThread].map((chat, index) => (
                    <div key={index} className="mb-4">
                      <p className="font-semibold">Q: {chat.question}</p>
                      <p className="text-gray-700">A: {chat.answer}</p>
                    </div>
                  ))}
                </div>
              )}
              <div className="flex items-center mb-4">
                <input
                  type="text"
                  value={question}
                  onChange={(e) => setQuestion(e.target.value)}
                  placeholder="Ask a question about your documents..."
                  onKeyPress={(e) => e.key === 'Enter' && !loading && handleAsk()}
                  className="flex-1 p-2 border rounded mr-2"
                />
                <button
                  onClick={handleAsk}
                  disabled={loading || !question.trim()}
                  className="bg-teal-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
                >
                  {loading ? 'Thinking...' : 'Ask'}
                </button>
              </div>
              {answer && (
                <div className="p-4 bg-gray-50 rounded border">
                  <h4 className="font-semibold mb-2">Answer:</h4>
                  <p>{answer}</p>
                </div>
              )}
            </div>
          </div>

          {/* Upload Modal */}
          {showUploadModal && (
            <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
              <div className="bg-white p-6 rounded-lg shadow-lg w-96">
                <h3 className="text-lg font-semibold mb-4">Upload Document</h3>
                <select
                  value={fileType}
                  onChange={(e) => setFileType(e.target.value)}
                  className="w-full p-2 border rounded mb-4"
                >
                  <option value="text">Text</option>
                  <option value="pdf">PDF</option>
                  <option value="image">Image</option>
                  <option value="csv">CSV</option>
                  <option value="excel">Excel</option>
                </select>
                {fileType === 'text' ? (
                  <textarea
                    value={uploadText}
                    onChange={(e) => setUploadText(e.target.value)}
                    placeholder="Paste your text here..."
                    className="w-full p-2 border rounded mb-4"
                  />
                ) : (
                  <input
                    type="file"
                    onChange={(e) => setSelectedFile(e.target.files[0])}
                    accept={getAcceptedTypes()}
                    className="mb-4"
                  />
                )}
                {fileType === 'pdf' && (
                  <input
                    type="number"
                    value={startPage}
                    onChange={(e) => setStartPage(Math.max(1, parseInt(e.target.value) || 1))}
                    min="1"
                    placeholder="Start Page"
                    className="w-full p-2 border rounded mb-4"
                  />
                )}
                {fileType === 'image' && (
                  <select
                    value={imageType}
                    onChange={(e) => setImageType(e.target.value)}
                    className="w-full p-2 border rounded mb-4"
                  >
                    <option value="ocr">Text Image (OCR)</option>
                    <option value="blip">Non-Text Image (Charts/Figures - BLIP Caption)</option>
                  </select>
                )}
                <div className="flex justify-end">
                  <button onClick={() => setShowUploadModal(false)} className="mr-2 bg-gray-500 text-white px-4 py-2 rounded">Cancel</button>
                  <button
                    onClick={handleUpload}
                    disabled={uploading || (fileType === 'text' ? !uploadText.trim() : !selectedFile)}
                    className="bg-green-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
                  >
                    {uploading ? 'Uploading...' : 'Upload'}
                  </button>
                </div>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
