import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { motion, AnimatePresence } from 'framer-motion'; // Add framer-motion for animations
import { 
  ChevronLeftIcon, 
  ChevronRightIcon, 
  PlusIcon, 
  UploadIcon, 
  LogoutIcon, 
  UserIcon, 
  ImageIcon, 
  SendIcon, 
  RefreshIcon,
  XIcon 
} from '@heroicons/react/24/outline'; // Use Heroicons for better icons

// UUID function remains the same
function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [role, setRole] = useState('cadre');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadText, setUploadText] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileType, setFileType] = useState('pdf');
  const [imageType, setImageType] = useState('ocr');
  const [startPage, setStartPage] = useState(1);
  const [conversations, setConversations] = useState({});
  const [currentThread, setCurrentThread] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true); // Default to open for better UX
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [isRagMode, setIsRagMode] = useState(true);
  const [tempImageBase64, setTempImageBase64] = useState(null);
  const [tempMimeType, setTempMimeType] = useState(null);
  const [tempImageProcessing, setTempImageProcessing] = useState(false);
  const [showTempImageModal, setShowTempImageModal] = useState(false);
  const [tempImageFile, setTempImageFile] = useState(null);
  const [tempImageType, setTempImageType] = useState('ocr');
  const fileInputRef = useRef(null);
  const chatContainerRef = useRef(null); // For auto-scrolling chat

  const API_BASE = 'http://localhost:5000';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchConversations();
      setIsAuthenticated(true);
    }
  }, []);

  useEffect(() => {
    // Auto-scroll to bottom of chat when new messages arrive
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [conversations, currentThread]);

  const fetchConversations = async () => {
    try {
      const res = await axios.get(`${API_BASE}/conversations`);
      setConversations(res.data);
    } catch (error) {
      console.error('Error fetching conversations:', error);
    }
  };

  const handleRegister = async () => {
    try {
      const res = await axios.post(`${API_BASE}/register`, {
        first_name: firstName,
        role,
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setShowRegister(false);
      setFirstName('');
      setRole('cadre');
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await axios.post(`${API_BASE}/login`, {
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setIsAuthenticated(false);
    setUser(null);
    setConversations({});
    setCurrentThread(null);
    setIsSidebarOpen(false);
  };

  const handleUpload = async () => {
    if (fileType === 'text' && !uploadText.trim()) {
      return alert('Text required');
    }
    if (fileType !== 'text' && !selectedFile) {
      return alert('File required');
    }

    setUploading(true);
    const formData = new FormData();
    if (fileType !== 'text') {
      formData.append('file', selectedFile);
    }
    if (fileType === 'pdf') {
      formData.append('startPage', startPage);
    }
    if (fileType === 'image') {
      formData.append('imageType', imageType);
    }
    if (fileType === 'text') {
      formData.append('text', uploadText);
    }

    try {
      const endpoint = fileType === 'text' ? '/upload-text' : '/upload-file';
      await axios.post(`${API_BASE}${endpoint}`, fileType === 'text' ? { text: uploadText } : formData, {
        headers: fileType !== 'text' ? { 'Content-Type': 'multipart/form-data' } : {},
      });
      alert(`${fileType.charAt(0).toUpperCase() + fileType.slice(1)} uploaded`);
      setSelectedFile(null);
      setUploadText('');
      setStartPage(1);
      setImageType('ocr');
      setFileType('pdf');
      setShowUploadModal(false);
    } catch (error) {
      alert('Upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
    }
  };

  const handleTempImageProcess = async () => {
    if (!tempImageFile) return;
    
    setTempImageProcessing(true);
    try {
      const reader = new FileReader();
      reader.onload = async () => {
        const fullDataUrl = reader.result;
        const base64 = fullDataUrl.split(',')[1]; // Base64 data without prefix
        
        if (tempImageType === 'ocr') {
          // Extract text using OCR
          try {
            const response = await axios.post(`${API_BASE}/process-temp-image-ocr`, {
              image: base64
            });
            const processedText = response.data.text || '';
            
            // Append OCR text to question
            setQuestion(prev => `${prev}\n\nExtracted text from image: ${processedText}`);
            alert('Text extracted from image and added to your question!');
          } catch (backendError) {
            console.warn('Backend OCR failed, trying direct API:', backendError);
            try {
              const EMBEDDINGS_API_BASE = 'http://localhost:8000';
              const response = await axios.post(`${EMBEDDINGS_API_BASE}/ocr`, {
                image: base64
              }, { timeout: 30000, headers: { 'Content-Type': 'application/json' } });
              const processedText = response.data.text || '';
              
              setQuestion(prev => `${prev}\n\nExtracted text from image: ${processedText}`);
              alert('Text extracted from image and added to your question!');
            } catch (directError) {
              console.error('Direct OCR API failed:', directError);
              alert('Failed to extract text from image: ' + (directError.response?.data?.error || directError.message));
            }
          }
        } else if (tempImageType === 'explain') {
          // Store image for AI analysis
          setTempImageBase64(base64);
          setTempMimeType(tempImageFile.type);
          alert('Image attached! Now ask a question about it â€” the AI will analyze the image along with your query.');
        }
        
        setShowTempImageModal(false);
        setTempImageFile(null);
      };
      reader.readAsDataURL(tempImageFile);
    } catch (error) {
      console.error('Image processing error:', error);
      alert('Failed to process image: ' + (error.response?.data?.error || error.message));
    } finally {
      setTempImageProcessing(false);
    }
  };

  const handleAsk = async () => {
    if (!question.trim() || loading) return;

    setLoading(true);
    const thread_id = currentThread || uuidv4();
    setCurrentThread(thread_id);

    try {
      const endpoint = isRagMode ? '/ask' : '/ask-general';
      const payload = isRagMode ? { question, thread_id } : { question, thread_id };

      // If in general mode and there's a temp image, add it to payload
      if (!isRagMode && tempImageBase64) {
        payload.image_base64 = tempImageBase64;
        payload.mime_type = tempMimeType;
      }

      const res = await axios.post(`${API_BASE}${endpoint}`, payload);

      const updatedConversations = { ...conversations };
      if (!updatedConversations[thread_id]) {
        updatedConversations[thread_id] = [];
      }
      updatedConversations[thread_id].push({
        question,
        answer: res.data.answer,
        sources: res.data.sources || [],
      });
      setConversations(updatedConversations);
      setQuestion('');

      // Clear temp image after use
      if (!isRagMode && tempImageBase64) {
        setTempImageBase64(null);
        setTempMimeType(null);
      }
    } catch (error) {
      console.error('Ask error:', error);
      alert('Error: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setLoading(false);
    }
  };

  const startNewThread = () => {
    setCurrentThread(null);
    setTempImageBase64(null);
    setTempMimeType(null);
  };

  const toggleMode = () => {
    setIsRagMode(!isRagMode);
    if (isRagMode) {
      setTempImageBase64(null);
      setTempMimeType(null);
    }
  };

  const handleTempImageSelect = (e) => {
    const file = e.target.files[0];
    if (file) {
      setTempImageFile(file);
      setShowTempImageModal(true);
    }
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  const formatTimestamp = (timestamp) => {
    return new Date(timestamp).toLocaleString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getThreadTitle = (threadId) => {
    const thread = conversations[threadId];
    if (!thread || thread.length === 0) return 'New Chat';
    return thread[0].question.substring(0, 30) + (thread[0].question.length > 30 ? '...' : '');
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-teal-50 flex items-center justify-center p-4">
        <motion.div 
          initial={{ opacity: 0, scale: 0.95 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.5 }}
          className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md"
        >
          <h2 className="text-2xl font-bold text-gray-800 mb-6 text-center">
            {showRegister ? 'Create Account' : 'Welcome Back'}
          </h2>
          
          {showRegister && (
            <>
              <input
                type="text"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                placeholder="First Name"
                className="w-full p-3 mb-4 border rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500 transition-all"
              />
              <select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                className="w-full p-3 mb-4 border rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500 transition-all"
              >
                <option value="cadre">Cadre</option>
                <option value="admin">Admin</option>
              </select>
            </>
          )}
          
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
            className="w-full p-3 mb-4 border rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500 transition-all"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            className="w-full p-3 mb-6 border rounded-lg focus:outline-none focus:ring-2 focus:ring-teal-500 transition-all"
          />
          
          <motion.button
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
            onClick={showRegister ? handleRegister : handleLogin}
            className="w-full bg-teal-500 text-white py-3 rounded-lg hover:bg-teal-600 transition-colors shadow-md"
          >
            {showRegister ? 'Register' : 'Login'}
          </motion.button>
          
          <button
            onClick={() => setShowRegister(!showRegister)}
            className="w-full text-teal-500 mt-4 hover:underline"
          >
            {showRegister ? 'Already have an account? Login' : "Don't have an account? Register"}
          </button>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-teal-50 flex overflow-hidden">
      {/* Sidebar */}
      <AnimatePresence>
        {isSidebarOpen && (
          <motion.div
            initial={{ x: -300 }}
            animate={{ x: 0 }}
            exit={{ x: -300 }}
            transition={{ type: 'spring', stiffness: 300, damping: 30 }}
            className="w-80 bg-white shadow-xl flex flex-col"
          >
            <div className="p-4 border-b flex items-center justify-between bg-teal-50">
              <h3 className="font-semibold text-lg text-gray-800">Chat History</h3>
              <motion.button
                whileHover={{ scale: 1.1 }}
                onClick={startNewThread}
                className="p-2 hover:bg-teal-100 rounded-full transition-colors"
              >
                <PlusIcon className="h-5 w-5 text-teal-600" />
              </motion.button>
            </div>
            <div className="flex-1 overflow-y-auto">
              {Object.keys(conversations).map((threadId) => (
                <motion.div
                  key={threadId}
                  whileHover={{ backgroundColor: '#f0fdfa' }}
                  onClick={() => setCurrentThread(threadId)}
                  className={`p-4 border-b cursor-pointer transition-colors ${
                    currentThread === threadId ? 'bg-teal-50' : ''
                  }`}
                >
                  <p className="font-medium text-gray-800 truncate">{getThreadTitle(threadId)}</p>
                  <p className="text-sm text-gray-500">
                    {formatTimestamp(conversations[threadId][0].timestamp)}
                  </p>
                </motion.div>
              ))}
            </div>
            <div className="p-4 border-t bg-teal-50">
              <div className="flex items-center space-x-3 mb-4">
                <div className="h-10 w-10 rounded-full bg-teal-200 flex items-center justify-center">
                  <UserIcon className="h-5 w-5 text-teal-600" />
                </div>
                <div>
                  <p className="font-medium text-gray-800">{user?.first_name || 'User'}</p>
                  <p className="text-sm text-gray-500 capitalize">{user?.role}</p>
                </div>
              </div>
              <motion.button
                whileHover={{ scale: 1.02 }}
                onClick={handleLogout}
                className="w-full bg-red-500 text-white py-2 rounded-lg hover:bg-red-600 transition-colors flex items-center justify-center space-x-2"
              >
                <LogoutIcon className="h-5 w-5" />
                <span>Logout</span>
              </motion.button>
            </div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <div className="bg-white shadow-md p-4 flex items-center justify-between">
          <motion.button
            whileHover={{ scale: 1.1 }}
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            className="p-2 hover:bg-teal-100 rounded-full transition-colors"
          >
            {isSidebarOpen ? <ChevronLeftIcon className="h-6 w-6 text-teal-600" /> : <ChevronRightIcon className="h-6 w-6 text-teal-600" />}
          </motion.button>
          <h1 className="text-xl font-bold text-gray-800">Intelligent Chat Assistant</h1>
          <motion.button
            whileHover={{ scale: 1.02 }}
            onClick={toggleMode}
            className={`px-4 py-2 rounded-full transition-colors shadow-md ${
              isRagMode ? 'bg-teal-500 text-white' : 'bg-blue-500 text-white'
            }`}
          >
            {isRagMode ? 'Document Mode' : 'General Mode'}
          </motion.button>
        </div>

        {/* Chat Area */}
        <div ref={chatContainerRef} className="flex-1 overflow-y-auto p-6 space-y-6 bg-gradient-to-b from-white to-teal-50">
          {currentThread && conversations[currentThread] ? (
            conversations[currentThread].map((msg, idx) => (
              <div key={idx} className="space-y-4">
                {/* User Message */}
                <motion.div 
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="flex justify-end"
                >
                  <div className="max-w-xl bg-teal-100 rounded-2xl p-4 shadow-md">
                    <p className="text-gray-800">{msg.question}</p>
                  </div>
                </motion.div>
                
                {/* AI Response */}
                <motion.div 
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  className="flex justify-start"
                >
                  <div className="max-w-xl bg-white rounded-2xl p-4 shadow-md">
                    <p className="text-gray-800 whitespace-pre-wrap">{msg.answer}</p>
                    {msg.sources && msg.sources.length > 0 && (
                      <div className="mt-4 pt-4 border-t">
                        <p className="text-sm font-medium text-gray-600 mb-2">Sources:</p>
                        <ul className="space-y-2">
                          {msg.sources.map((source, sIdx) => (
                            <li key={sIdx} className="text-sm text-teal-600 truncate">
                              {source.file_name} (Page {source.page_number}): {source.chunk_text.substring(0, 50)}...
                            </li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                </motion.div>
              </div>
            ))
          ) : (
            <motion.div 
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="flex flex-col items-center justify-center h-full text-center text-gray-500"
            >
              <RefreshIcon className="h-16 w-16 mb-4 animate-spin-slow" />
              <p className="text-lg">Start a new conversation</p>
              <p className="text-sm mt-2">Ask anything or upload documents to begin</p>
            </motion.div>
          )}
        </div>

        {/* Input Area */}
        <div className="bg-white p-4 shadow-lg">
          <div className="max-w-4xl mx-auto flex items-center space-x-3">
            {!isRagMode && (
              <>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleTempImageSelect}
                  ref={fileInputRef}
                  className="hidden"
                />
                <motion.button
                  whileHover={{ scale: 1.1 }}
                  onClick={() => fileInputRef.current.click()}
                  className="p-3 bg-teal-100 rounded-full hover:bg-teal-200 transition-colors"
                >
                  <ImageIcon className="h-5 w-5 text-teal-600" />
                </motion.button>
              </>
            )}
            
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder={
                isRagMode 
                  ? "Ask a question about your documents..." 
                  : "Ask me anything (general conversation)..."
              }
              onKeyPress={(e) => e.key === 'Enter' && !loading && handleAsk()}
              className="flex-1 p-3 border rounded-full focus:outline-none focus:ring-2 focus:ring-teal-500 shadow-sm transition-all"
            />
            
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={handleAsk}
              disabled={loading || !question.trim()}
              className="bg-teal-500 text-white p-3 rounded-full hover:bg-teal-600 disabled:bg-gray-300 disabled:cursor-not-allowed transition-colors shadow-md"
            >
              {loading ? (
                <RefreshIcon className="h-5 w-5 animate-spin" />
              ) : (
                <SendIcon className="h-5 w-5" />
              )}
            </motion.button>
          </div>
        </div>
      </div>

      {/* Floating Upload Button - Only in RAG mode */}
      {isRagMode && (
        <motion.button
          initial={{ y: 100 }}
          animate={{ y: 0 }}
          whileHover={{ scale: 1.1 }}
          onClick={() => setShowUploadModal(true)}
          className="fixed bottom-6 right-6 bg-green-500 text-white p-4 rounded-full shadow-lg hover:bg-green-600 transition-colors z-20"
        >
          <UploadIcon className="h-6 w-6" />
        </motion.button>
      )}

      {/* Upload Modal */}
      <AnimatePresence>
        {showUploadModal && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
          >
            <motion.div
              initial={{ scale: 0.95, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.95, opacity: 0 }}
              className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md"
            >
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold text-gray-800">Upload Document</h3>
                <button onClick={() => setShowUploadModal(false)}>
                  <XIcon className="h-5 w-5 text-gray-500" />
                </button>
              </div>
              
              <select
                value={fileType}
                onChange={(e) => setFileType(e.target.value)}
                className="w-full p-3 border rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-green-500 transition-all"
              >
                <option value="pdf">PDF</option>
                <option value="image">Image</option>
                <option value="csv">CSV</option>
                <option value="excel">Excel</option>
                <option value="text">Text</option>
              </select>

              {fileType === 'text' && (
                <textarea
                  value={uploadText}
                  onChange={(e) => setUploadText(e.target.value)}
                  placeholder="Paste your text here..."
                  className="w-full p-3 border rounded-lg mb-4 h-32 resize-none focus:outline-none focus:ring-2 focus:ring-green-500 transition-all"
                />
              )}

              {fileType === 'pdf' && (
                <input
                  type="number"
                  value={startPage}
                  onChange={(e) => setStartPage(Math.max(1, parseInt(e.target.value) || 1))}
                  min="1"
                  placeholder="Start Page"
                  className="w-full p-3 border rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-green-500 transition-all"
                />
              )}

              {fileType === 'image' && (
                <select
                  value={imageType}
                  onChange={(e) => setImageType(e.target.value)}
                  className="w-full p-3 border rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-green-500 transition-all"
                >
                  <option value="ocr">Text Image (OCR)</option>
                  <option value="blip">Non-Text Image (Charts/Figures - BLIP Caption)</option>
                </select>
              )}

              {fileType !== 'text' && (
                <input
                  type="file"
                  onChange={(e) => setSelectedFile(e.target.files[0])}
                  accept={
                    fileType === 'pdf'
                      ? '.pdf'
                      : fileType === 'image'
                      ? 'image/*'
                      : fileType === 'csv'
                      ? '.csv'
                      : '.xlsx,.xls'
                  }
                  className="w-full mb-4 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-green-50 file:text-green-700 hover:file:bg-green-100"
                />
              )}

              <div className="flex justify-end space-x-3">
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  onClick={() => setShowUploadModal(false)}
                  className="px-4 py-2 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 transition-colors"
                >
                  Cancel
                </motion.button>
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  onClick={handleUpload}
                  disabled={uploading || (fileType !== 'text' && !selectedFile) || (fileType === 'text' && !uploadText.trim())}
                  className="px-4 py-2 bg-green-500 text-white rounded-lg hover:bg-green-600 disabled:bg-gray-300 disabled:cursor-not-allowed transition-colors"
                >
                  {uploading ? 'Uploading...' : 'Upload'}
                </motion.button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Temporary Image Modal */}
      <AnimatePresence>
        {showTempImageModal && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50"
          >
            <motion.div
              initial={{ scale: 0.95, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.95, opacity: 0 }}
              className="bg-white rounded-2xl shadow-xl p-6 w-full max-w-md"
            >
              <div className="flex justify-between items-center mb-4">
                <h3 className="text-lg font-semibold text-gray-800">Process Image</h3>
                <button onClick={() => { setShowTempImageModal(false); setTempImageFile(null); }}>
                  <XIcon className="h-5 w-5 text-gray-500" />
                </button>
              </div>
              
              <p className="text-sm text-gray-600 mb-4">
                This image will be processed temporarily and not saved.
              </p>
              
              {tempImageFile && (
                <div className="mb-4 p-3 bg-gray-50 rounded-lg">
                  <p className="text-sm font-medium text-gray-700">Selected: {tempImageFile.name}</p>
                </div>
              )}

              <select
                value={tempImageType}
                onChange={(e) => setTempImageType(e.target.value)}
                className="w-full p-3 border rounded-lg mb-4 focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all"
              >
                <option value="ocr">Extract text (OCR)</option>
                <option value="explain">Analyze image with AI</option>
              </select>

              <p className="text-sm text-gray-600 mb-4">
                {tempImageType === 'ocr' 
                  ? "Text will be extracted and added to your question."
                  : "The AI will analyze the image with your question."}
              </p>

              <div className="flex justify-end space-x-3">
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  onClick={() => { setShowTempImageModal(false); setTempImageFile(null); }}
                  className="px-4 py-2 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 transition-colors"
                >
                  Cancel
                </motion.button>
                <motion.button
                  whileHover={{ scale: 1.02 }}
                  onClick={handleTempImageProcess}
                  disabled={tempImageProcessing}
                  className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 disabled:bg-gray-300 disabled:cursor-not-allowed transition-colors"
                >
                  {tempImageProcessing ? 'Processing...' : 'Process'}
                </motion.button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default App;
