import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

// Add UUID function at the top
function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    var r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

// Icons (optional, but good for aesthetics)
const PlusIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 6v6m0 0v6m0-6h6m-6 0H6" />
  </svg>
);

const HistoryIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
  </svg>
);

const NewChatIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2" viewBox="0 0 20 20" fill="currentColor">
    <path d="M2 5a2 2 0 012-2h7a2 2 0 012 2v4a2 2 0 01-2 2H9l-3 3v-3H4a2 2 0 01-2-2V5z" />
    <path d="M15 7v2a4 4 0 01-4 4H9.833l-1.553 1.553A.25.25 0 018 15v-3H6v-2h2v-2h2v2h4z" />
  </svg>
);

const UploadIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 16a4 4 0 01-4-4v-1a4 4 0 014-4h4m6 4-3-3m0 0-3 3m3-3v12" />
  </svg>
);

const ChatBubbleIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-blue-500" viewBox="0 0 20 20" fill="currentColor">
    <path fillRule="evenodd" d="M18 10c0 3.866-3.582 7-8 7a8.841 8.841 0 01-4.083-.98L2 17l1.338-3.123C2.493 12.767 2 11.434 2 10c0-3.866 3.582-7 8-7s8 3.134 8 7zM7 9H5v2h2V9zm8 0h-2v2h2V9zM9 9h2v2H9V9z" clipRule="evenodd" />
  </svg>
);

const UserIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 mr-2 text-teal-600" viewBox="0 0 20 20" fill="currentColor">
    <path fillRule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clipRule="evenodd" />
  </svg>
);

const SendIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
  </svg>
);

const PhotoIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
  </svg>
);

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [role, setRole] = useState('cadre');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadText, setUploadText] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileType, setFileType] = useState('pdf');
  const [imageType, setImageType] = useState('ocr');
  const [startPage, setStartPage] = useState(1);
  const [conversations, setConversations] = useState({});
  const [currentThread, setCurrentThread] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [isRagMode, setIsRagMode] = useState(true);
  const [tempImageBase64, setTempImageBase64] = useState(null);
  const [tempMimeType, setTempMimeType] = useState(null);
  const [tempImageProcessing, setTempImageProcessing] = useState(false);
  const [showTempImageModal, setShowTempImageModal] = useState(false);
  const [tempImageFile, setTempImageFile] = useState(null);
  const [tempImageType, setTempImageType] = useState('ocr');
  const fileInputRef = useRef(null);

  const API_BASE = 'http://localhost:5000';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchConversations();
      setIsAuthenticated(true);
    }
  }, []);

  const fetchConversations = async () => {
    try {
      const res = await axios.get(`${API_BASE}/conversations`);
      setConversations(res.data);
    } catch (error) {
      console.error('Error fetching conversations:', error);
    }
  };

  const handleRegister = async () => {
    try {
      const res = await axios.post(`${API_BASE}/register`, {
        first_name: firstName,
        role,
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setShowRegister(false);
      setFirstName('');
      setRole('cadre');
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await axios.post(`${API_BASE}/login`, {
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setIsAuthenticated(false);
    setUser(null);
    setConversations({});
    setCurrentThread(null);
    setIsSidebarOpen(false);
  };

  const handleUpload = async () => {
    if (fileType === 'text' && !uploadText.trim()) {
      return alert('Text required');
    }
    if (fileType !== 'text' && !selectedFile) {
      return alert('File required');
    }

    setUploading(true);
    const formData = new FormData();
    if (fileType !== 'text') {
      formData.append('file', selectedFile);
    }
    if (fileType === 'pdf') {
      formData.append('startPage', startPage);
    }
    if (fileType === 'image') {
      formData.append('imageType', imageType);
    }
    if (fileType === 'text') {
      formData.append('text', uploadText);
    }

    try {
      const endpoint = fileType === 'text' ? '/upload-text' : '/upload-file';
      await axios.post(`${API_BASE}${endpoint}`, fileType === 'text' ? { text: uploadText } : formData, {
        headers: fileType !== 'text' ? { 'Content-Type': 'multipart/form-data' } : {},
      });
      alert(`${fileType.charAt(0).toUpperCase() + fileType.slice(1)} uploaded`);
      setSelectedFile(null);
      setUploadText('');
      setStartPage(1);
      setImageType('ocr');
      setFileType('pdf');
      setShowUploadModal(false);
    } catch (error) {
      alert('Upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
    }
  };

  const handleTempImageProcess = async () => {
    if (!tempImageFile) return;
    
    setTempImageProcessing(true);
    try {
      const reader = new FileReader();
      reader.onload = async () => {
        const fullDataUrl = reader.result;
        const base64 = fullDataUrl.split(',')[1];
        
        if (tempImageType === 'ocr') {
          try {
            const response = await axios.post(`${API_BASE}/process-temp-image-ocr`, {
              image: base64
            });
            const processedText = response.data.text || '';
            
            setQuestion(prev => `${prev}\n\nExtracted text from image: ${processedText}`);
            alert('Text extracted from image and added to your question!');
          } catch (backendError) {
            console.warn('Backend OCR failed, trying direct API:', backendError);
            try {
              const EMBEDDINGS_API_BASE = 'http://localhost:8000';
              const response = await axios.post(`${EMBEDDINGS_API_BASE}/ocr`, {
                image: base64
              }, { timeout: 30000, headers: { 'Content-Type': 'application/json' } });
              const processedText = response.data.text || '';
              
              setQuestion(prev => `${prev}\n\nExtracted text from image: ${processedText}`);
              alert('Text extracted from image and added to your question!');
            } catch (directError) {
              console.error('Direct OCR API failed:', directError);
              alert('Failed to extract text from image: ' + (directError.response?.data?.error || directError.message));
            }
          }
        } else if (tempImageType === 'explain') {
          setTempImageBase64(base64);
          setTempMimeType(tempImageFile.type);
          alert('Image attached! Now ask a question about it â€” the AI will analyze the image along with your query.');
        }
        
        setShowTempImageModal(false);
        setTempImageFile(null);
      };
      reader.readAsDataURL(tempImageFile);
    } catch (error) {
      console.error('Image processing error:', error);
      alert('Failed to process image: ' + (error.response?.data?.error || error.message));
    } finally {
      setTempImageProcessing(false);
    }
  };

  const handleAsk = async () => {
    if (!question.trim()) return;
    
    setLoading(true);
    const threadId = currentThread || uuidv4();
    if (!currentThread) setCurrentThread(threadId);
    
    const newQA = { question, answer: '', sources: [] };
    setConversations(prev => ({
      ...prev,
      [threadId]: [...(prev[threadId] || []), newQA]
    }));
    
    try {
      const payload = {
        question,
        thread_id: threadId,
        force_general_mode: !isRagMode
      };
      
      if (!isRagMode && tempImageBase64 && tempMimeType) {
        payload.image_base64 = tempImageBase64;
        payload.mime_type = tempMimeType;
      }
      
      const res = await axios.post(`${API_BASE}/ask`, payload);
      
      setConversations(prev => {
        const updatedConversations = { ...prev };
        const thread = updatedConversations[threadId];
        
        if (thread && thread.length > 0) {
          thread[thread.length - 1].answer = res.data.answer;
          thread[thread.length - 1].sources = res.data.sources || [];
        }
        
        return updatedConversations;
      });
      
      setTempImageBase64(null);
      setTempMimeType(null);
    } catch (error) {
      setConversations(prev => {
        const updatedConversations = { ...prev };
        const thread = updatedConversations[threadId];
        
        if (thread && thread.length > 0) {
          thread[thread.length - 1].answer = 'Error: ' + (error.response ? error.response.data.error : error.message);
        }
        
        return updatedConversations;
      });
    } finally {
      setLoading(false);
      setQuestion('');
    }
  };

  const startNewConversation = () => {
    setCurrentThread(null);
  };

  const toggleRagMode = () => {
    setIsRagMode(!isRagMode);
    setTempImageBase64(null);
    setTempMimeType(null);
  };

  const handleTempImageUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('image/')) {
      setTempImageFile(file);
      setShowTempImageModal(true);
    } else {
      alert('Please select a valid image file');
    }
    event.target.value = '';
  };

  return (
    <div className="min-h-screen bg-gray-50 font-sans antialiased text-gray-800">
      {!isAuthenticated ? (
        <div className="flex items-center justify-center min-h-screen bg-gradient-to-br from-blue-500 to-teal-500">
          <div className="bg-white p-10 rounded-xl shadow-2xl w-full max-w-md transform transition-all hover:scale-105 duration-300">
            <h2 className="text-3xl font-extrabold text-center text-gray-900 mb-6">{showRegister ? 'Join Us' : 'Welcome Back'}</h2>
            {showRegister && (
              <>
                <input
                  type="text"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                  placeholder="First Name"
                  className="w-full p-3 mb-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 transition-colors"
                />
                <select
                  value={role}
                  onChange={(e) => setRole(e.target.value)}
                  className="w-full p-3 mb-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 transition-colors bg-white appearance-none"
                >
                  <option value="cadre">Cadre</option>
                  <option value="admin">Admin</option>
                </select>
              </>
            )}
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Username"
              className="w-full p-3 mb-4 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 transition-colors"
            />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              className="w-full p-3 mb-6 border border-gray-300 rounded-lg focus:ring-2 focus:ring-teal-500 transition-colors"
            />
            <button
              onClick={showRegister ? handleRegister : handleLogin}
              className="w-full bg-blue-600 text-white font-bold p-3 rounded-lg hover:bg-blue-700 transition-colors duration-300 shadow-md"
            >
              {showRegister ? 'Register' : 'Log In'}
            </button>
            <p className="mt-6 text-center text-sm text-gray-600">
              {showRegister ? 'Already have an account?' : 'Need an account?'}{' '}
              <span
                onClick={() => setShowRegister(!showRegister)}
                className="text-blue-600 font-semibold cursor-pointer hover:underline transition-colors"
              >
                {showRegister ? 'Log in' : 'Register'}
              </span>
            </p>
          </div>
        </div>
      ) : (
        <div className="relative flex min-h-screen bg-gray-50">
          {/* Sidebar */}
          <div
            className={`fixed inset-y-0 left-0 w-72 bg-gray-900 text-gray-100 shadow-xl transform ${
              isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
            } transition-transform duration-300 ease-in-out z-20 md:relative md:translate-x-0 md:flex-shrink-0`}
          >
            <div className="p-6 h-full flex flex-col">
              <div className="flex justify-between items-center mb-6">
                <h3 className="text-xl font-semibold">Conversations</h3>
                <button
                  onClick={() => setIsSidebarOpen(false)}
                  className="md:hidden text-gray-400 hover:text-white transition-colors"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                  </svg>
                </button>
              </div>
              <button
                onClick={() => {
                  startNewConversation();
                  setIsSidebarOpen(false);
                }}
                className="flex items-center w-full px-4 py-3 mb-4 text-left font-medium text-white bg-teal-600 rounded-lg shadow-md hover:bg-teal-700 transition-colors duration-200"
              >
                <NewChatIcon /> New Conversation
              </button>
              <div className="flex-1 overflow-y-auto space-y-2">
                {Object.keys(conversations).length === 0 ? (
                  <p className="text-gray-400 text-sm">No conversations yet. Start one!</p>
                ) : (
                  Object.keys(conversations).map((thread) => (
                    <button
                      key={thread}
                      onClick={() => {
                        setCurrentThread(thread);
                        setIsSidebarOpen(false);
                      }}
                      className={`group flex items-center w-full text-left p-3 rounded-lg transition-colors duration-200 ${
                        currentThread === thread
                          ? 'bg-gray-700 text-white'
                          : 'bg-gray-800 hover:bg-gray-700 text-gray-300'
                      }`}
                    >
                      <ChatBubbleIcon />
                      <span className="truncate flex-1">
                        {conversations[thread][0]?.question.slice(0, 30) || `Thread ${thread.slice(0, 8)}...`}
                      </span>
                    </button>
                  ))
                )}
              </div>
            </div>
          </div>

          {/* Main Content */}
          <div className="flex-1 flex flex-col min-h-screen">
            <header className="sticky top-0 bg-white shadow-sm z-10 p-4 border-b border-gray-200">
              <div className="container mx-auto flex justify-between items-center">
                <div className="flex items-center">
                  <button
                    onClick={() => setIsSidebarOpen(true)}
                    className="p-2 text-gray-600 hover:bg-gray-100 rounded-full md:hidden"
                    title="Toggle History"
                  >
                    <HistoryIcon />
                  </button>
                  <div className="ml-2 md:ml-0">
                    <h1 className="text-2xl font-bold text-gray-900">HCP Chatbot</h1>
                    <p className="text-sm text-gray-500">Hello, {user?.username || 'User'}!</p>
                  </div>
                </div>
                <button
                  onClick={handleLogout}
                  className="px-4 py-2 text-sm font-semibold text-white bg-red-500 rounded-lg hover:bg-red-600 transition-colors duration-200"
                >
                  Logout
                </button>
              </div>
            </header>

            <main className="flex-1 p-4 md:p-8 overflow-y-auto">
              <div className="container mx-auto h-full flex flex-col">
                <div className="bg-white p-6 rounded-xl shadow-lg mb-6">
                  <div className="flex items-center justify-between">
                    <div>
                      <h4 className="font-bold text-gray-700">Answer Mode</h4>
                      <p className="text-sm text-gray-500 max-w-lg mt-1">
                        {isRagMode 
                          ? "Answers are based on your uploaded documents (RAG Mode)." 
                          : "General knowledge mode, similar to ChatGPT."
                        }
                      </p>
                    </div>
                    <div className="flex items-center">
                      <span className={`text-sm font-medium ${!isRagMode ? 'text-purple-600' : 'text-gray-400'} transition-colors`}>
                        General
                      </span>
                      <label className="relative inline-flex items-center cursor-pointer mx-3">
                        <input
                          type="checkbox"
                          checked={isRagMode}
                          onChange={toggleRagMode}
                          className="sr-only peer"
                        />
                        <div className="w-11 h-6 bg-gray-200 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-teal-600"></div>
                      </label>
                      <span className={`text-sm font-medium ${isRagMode ? 'text-teal-600' : 'text-gray-400'} transition-colors`}>
                        Documents
                      </span>
                    </div>
                  </div>
                </div>

                <div className="bg-white rounded-xl shadow-lg p-6 flex-1 flex flex-col min-h-0">
                  <div className="flex-1 overflow-y-auto space-y-6 pr-4">
                    {currentThread && conversations[currentThread] && (
                      conversations[currentThread].map((chat, index) => (
                        <div key={index} className="border-b border-gray-200 pb-4 last:border-b-0">
                          <div className="flex items-start mb-2">
                            <UserIcon />
                            <p className="font-semibold text-gray-900 leading-relaxed">
                              {chat.question}
                            </p>
                          </div>
                          <div className="flex items-start">
                            <ChatBubbleIcon />
                            <div className="text-gray-700 leading-relaxed flex-1">
                              {chat.answer}
                            </div>
                          </div>
                          {chat.sources && chat.sources.length > 0 && (
                            <div className="mt-2 text-sm text-gray-500 leading-tight">
                              <span className="font-medium">Sources:</span>{' '}
                              {chat.sources.map((s) => `${s.file} (p. ${s.page})`).join(', ')}
                            </div>
                          )}
                        </div>
                      ))
                    )}
                    
                    {(!currentThread || !conversations[currentThread]) && (
                      <div className="flex items-center justify-center h-full text-gray-400 text-center">
                        <p className="text-lg">
                          Start a conversation by asking a question below.
                          <br />
                          {isRagMode ? "I'll use your documents for answers." : "I'll use general knowledge."}
                        </p>
                      </div>
                    )}
                  </div>

                  {/* Question Input */}
                  <div className="flex items-end gap-3 mt-6 pt-6 border-t border-gray-200">
                    {!isRagMode && (
                      <>
                        <input
                          ref={fileInputRef}
                          type="file"
                          accept="image/*"
                          onChange={handleTempImageUpload}
                          className="hidden"
                        />
                        <button
                          onClick={() => fileInputRef.current?.click()}
                          className="p-3 text-gray-500 bg-gray-100 rounded-full hover:bg-gray-200 transition-colors duration-200"
                          title="Attach image"
                          disabled={loading || tempImageProcessing}
                        >
                          <PhotoIcon />
                        </button>
                      </>
                    )}
                    
                    <div className="flex-1 relative">
                      {tempImageBase64 && (
                        <div className="absolute -top-10 left-0 bg-green-50 text-green-700 px-3 py-1 rounded-full text-xs font-medium flex items-center space-x-2 shadow">
                          <span>Image Attached</span>
                          <button
                            onClick={() => { setTempImageBase64(null); setTempMimeType(null); }}
                            className="text-green-700 hover:text-green-900 transition-colors"
                          >
                            &times;
                          </button>
                        </div>
                      )}
                      <textarea
                        rows="1"
                        value={question}
                        onChange={(e) => setQuestion(e.target.value)}
                        placeholder={isRagMode ? "Ask about your documents..." : "Ask a general question..."}
                        onKeyPress={(e) => e.key === 'Enter' && !loading && handleAsk()}
                        className="w-full p-4 pr-16 bg-gray-100 border border-transparent rounded-full resize-none focus:outline-none focus:ring-2 focus:ring-teal-500 transition-all duration-200 overflow-hidden"
                        style={{ minHeight: '52px', maxHeight: '150px' }}
                      />
                      <button
                        onClick={handleAsk}
                        disabled={loading || !question.trim()}
                        className="absolute right-2 bottom-2 p-2 bg-teal-500 text-white rounded-full hover:bg-teal-600 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors duration-200"
                      >
                        <SendIcon />
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </main>
          </div>

          {/* Floating Upload Button - Only show in RAG mode */}
          {isRagMode && (
            <button
              onClick={() => setShowUploadModal(true)}
              className="fixed bottom-6 right-6 p-4 text-white bg-green-500 rounded-full shadow-lg hover:bg-green-600 transition-colors duration-200 transform hover:scale-110"
              title="Upload Document"
            >
              <UploadIcon />
            </button>
          )}

          {/* Upload Modal */}
          {showUploadModal && (
            <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50 p-4">
              <div className="bg-white p-8 rounded-xl shadow-xl w-full max-w-lg transform transition-all scale-95 opacity-0 animate-scale-in">
                <h3 className="text-xl font-bold mb-4 text-gray-900">Upload Document</h3>
                <select
                  value={fileType}
                  onChange={(e) => setFileType(e.target.value)}
                  className="w-full p-3 mb-4 border border-gray-300 rounded-lg bg-white"
                >
                  <option value="pdf">PDF</option>
                  <option value="image">Image</option>
                  <option value="csv">CSV</option>
                  <option value="excel">Excel</option>
                  <option value="text">Text</option>
                </select>
                {fileType === 'text' && (
                  <textarea
                    value={uploadText}
                    onChange={(e) => setUploadText(e.target.value)}
                    placeholder="Paste your text here..."
                    className="w-full p-3 mb-4 border border-gray-300 rounded-lg h-32 resize-vertical"
                  />
                )}
                {fileType === 'pdf' && (
                  <input
                    type="number"
                    value={startPage}
                    onChange={(e) => setStartPage(Math.max(1, parseInt(e.target.value) || 1))}
                    min="1"
                    placeholder="Start Page"
                    className="w-full p-3 mb-4 border border-gray-300 rounded-lg"
                  />
                )}
                {fileType === 'image' && (
                  <select
                    value={imageType}
                    onChange={(e) => setImageType(e.target.value)}
                    className="w-full p-3 mb-4 border border-gray-300 rounded-lg bg-white"
                  >
                    <option value="ocr">Text Image (OCR)</option>
                    <option value="blip">Non-Text Image (Charts/Figures - BLIP Caption)</option>
                  </select>
                )}
                {fileType !== 'text' && (
                  <input
                    type="file"
                    onChange={(e) => setSelectedFile(e.target.files[0])}
                    accept={
                      fileType === 'pdf'
                        ? '.pdf'
                        : fileType === 'image'
                        ? 'image/*'
                        : fileType === 'csv'
                        ? '.csv'
                        : '.xlsx,.xls'
                    }
                    className="mb-6 w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-blue-50 file:text-blue-700 hover:file:bg-blue-100"
                  />
                )}
                <div className="flex justify-end space-x-3">
                  <button
                    onClick={() => setShowUploadModal(false)}
                    className="px-6 py-2 text-gray-700 font-semibold bg-gray-200 rounded-lg hover:bg-gray-300 transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={handleUpload}
                    disabled={uploading || (fileType !== 'text' && !selectedFile) || (fileType === 'text' && !uploadText.trim())}
                    className="px-6 py-2 text-white font-semibold bg-green-600 rounded-lg hover:bg-green-700 transition-colors disabled:bg-gray-400 disabled:cursor-not-allowed"
                  >
                    {uploading ? 'Uploading...' : 'Upload'}
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Temporary Image Processing Modal */}
          {showTempImageModal && (
            <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-50 p-4">
              <div className="bg-white p-8 rounded-xl shadow-xl w-full max-w-lg transform transition-all scale-95 opacity-0 animate-scale-in">
                <h3 className="text-xl font-bold mb-4 text-gray-900">Process Image</h3>
                <p className="text-sm text-gray-600 mb-4">
                  This image will be processed temporarily and not saved to the database.
                </p>
                {tempImageFile && (
                  <div className="mb-4 p-4 border border-gray-200 rounded-lg bg-gray-50">
                    <p className="text-sm font-medium text-gray-700">Selected file:</p>
                    <p className="text-sm text-gray-600 truncate">{tempImageFile.name}</p>
                  </div>
                )}
                <select
                  value={tempImageType}
                  onChange={(e) => setTempImageType(e.target.value)}
                  className="w-full p-3 mb-4 border border-gray-300 rounded-lg bg-white"
                >
                  <option value="ocr">Extract text (OCR)</option>
                  <option value="explain">Analyze image with AI</option>
                </select>
                <div className="text-sm text-gray-600 mb-6 p-4 bg-blue-50 border border-blue-200 rounded-lg">
                  {tempImageType === 'ocr' 
                    ? "The text from the image will be extracted and automatically added to your question."
                    : "The AI will be able to 'see' and analyze the image to help answer your question."
                  }
                </div>
                <div className="flex justify-end space-x-3">
                  <button
                    onClick={() => {
                      setShowTempImageModal(false);
                      setTempImageFile(null);
                    }}
                    className="px-6 py-2 text-gray-700 font-semibold bg-gray-200 rounded-lg hover:bg-gray-300 transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={handleTempImageProcess}
                    disabled={tempImageProcessing}
                    className="px-6 py-2 text-white font-semibold bg-blue-600 rounded-lg hover:bg-blue-700 transition-colors disabled:bg-gray-400 disabled:cursor-not-allowed"
                  >
                    {tempImageProcessing ? 'Processing...' : 'Process Image'}
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
