import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { v4 as uuidv4 } from 'uuid';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [role, setRole] = useState('cadre');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState(''); // New state to track the current answer
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);
  const [uploadText, setUploadText] = useState('');
  const [selectedPdf, setSelectedPdf] = useState(null);
  const [selectedImage, setSelectedImage] = useState(null);
  const [imageType, setImageType] = useState('ocr');
  const [selectedCsv, setSelectedCsv] = useState(null);
  const [selectedExcel, setSelectedExcel] = useState(null);
  const [startPage, setStartPage] = useState(1);
  const [conversations, setConversations] = useState({});
  const [currentThread, setCurrentThread] = useState(null);
  const [showSidebar, setShowSidebar] = useState(false);
  const [showUploadModal, setShowUploadModal] = useState(false);
  const [uploadType, setUploadType] = useState(null);

  const API_BASE = 'http://localhost:5000';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchUserAndConversations();
    } else {
      setLoading(false);
    }
  }, []);

  const fetchUserAndConversations = async () => {
    try {
      const [userRes, convRes] = await Promise.all([
        axios.get(`${API_BASE}/user`),
        axios.get(`${API_BASE}/conversations`)
      ]);
      setUser(userRes.data);
      // Debug the received conversation data
      console.log('Conversation data:', convRes.data);
      // Handle the response, expecting an array of conversation objects
      const formattedConversations = convRes.data.reduce((acc, row) => {
        if (row.user_id === userRes.data.id) { // Filter by current user
          if (!acc[row.thread_id]) {
            acc[row.thread_id] = [];
          }
          acc[row.thread_id].push({
            question: row.question,
            answer: row.answer || 'No answer yet',
            timestamp: new Date(row.created_at)
          });
        }
        return acc;
      }, {});
      setConversations(formattedConversations);
      setIsAuthenticated(true);
    } catch (error) {
      console.error('Error fetching user or conversations:', error);
      if (error.response && error.response.status === 401) {
        localStorage.removeItem('token');
        setIsAuthenticated(false);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleRegister = async () => {
    try {
      const res = await axios.post(`${API_BASE}/register`, {
        first_name: firstName,
        role,
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setShowRegister(false);
      setFirstName('');
      setRole('cadre');
      setUsername('');
      setPassword('');
      fetchUserAndConversations();
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await axios.post(`${API_BASE}/login`, {
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setUsername('');
      setPassword('');
      await fetchUserAndConversations();
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setIsAuthenticated(false);
    setUser(null);
    setConversations({});
    setCurrentThread(null);
  };

  const handleUploadText = async () => {
    if (!uploadText.trim()) return alert('Text required');
    setUploading(true);
    try {
      await axios.post(`${API_BASE}/upload-text`, { text: uploadText });
      alert('Text uploaded');
      setUploadText('');
    } catch (error) {
      alert('Upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
      setShowUploadModal(false);
    }
  };

  const handleUploadPdf = async () => {
    if (!selectedPdf) return alert('PDF file required');
    setUploading(true);
    const formData = new FormData();
    formData.append('file', selectedPdf);
    formData.append('startPage', startPage);
    try {
      await axios.post(`${API_BASE}/upload-file`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('PDF uploaded');
      setSelectedPdf(null);
      setStartPage(1);
    } catch (error) {
      alert('PDF upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
      setShowUploadModal(false);
    }
  };

  const handleUploadImage = async () => {
    if (!selectedImage) return alert('Image file required');
    setUploading(true);
    const formData = new FormData();
    formData.append('file', selectedImage);
    formData.append('imageType', imageType);
    try {
      await axios.post(`${API_BASE}/upload-file`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('Image uploaded and processed');
      setSelectedImage(null);
      setImageType('ocr');
    } catch (error) {
      alert('Image upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
      setShowUploadModal(false);
    }
  };

  const handleUploadCsv = async () => {
    if (!selectedCsv) return alert('CSV file required');
    setUploading(true);
    const formData = new FormData();
    formData.append('file', selectedCsv);
    try {
      await axios.post(`${API_BASE}/upload-file`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('CSV uploaded');
      setSelectedCsv(null);
    } catch (error) {
      alert('CSV upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
      setShowUploadModal(false);
    }
  };

  const handleUploadExcel = async () => {
    if (!selectedExcel) return alert('Excel file required');
    setUploading(true);
    const formData = new FormData();
    formData.append('file', selectedExcel);
    try {
      await axios.post(`${API_BASE}/upload-file`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('Excel uploaded');
      setSelectedExcel(null);
    } catch (error) {
      alert('Excel upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
      setShowUploadModal(false);
    }
  };

  const handleAsk = async () => {
    if (!question.trim()) return;
    const threadId = currentThread || uuidv4();
    setCurrentThread(threadId);

    // Optimistically add the question with a placeholder
    setConversations(prev => {
      const updated = { ...prev };
      if (!updated[threadId]) {
        updated[threadId] = [];
      }
      updated[threadId].push({
        question: question,
        answer: 'Thinking...',
        timestamp: new Date()
      });
      return updated;
    });

    setLoading(true);
    try {
      const res = await axios.post(`${API_BASE}/ask`, {
        question,
        thread_id: threadId,
      });
      // Update with the real answer
      setConversations(prev => {
        const updated = { ...prev };
        const chatIndex = updated[threadId].length - 1;
        updated[threadId][chatIndex].answer = res.data.answer;
        updated[threadId][chatIndex].sources = res.data.sources || [];
        return updated;
      });
    } catch (error) {
      setConversations(prev => {
        const updated = { ...prev };
        const chatIndex = updated[threadId].length - 1;
        updated[threadId][chatIndex].answer = 'Error: ' + (error.response ? error.response.data.error : error.message);
        return updated;
      });
    } finally {
      setLoading(false);
      fetchUserAndConversations(); // Sync with server
    }
  };

  const startNewConversation = () => {
    setCurrentThread(null);
    setAnswer(''); // Clear current answer
  };

  const selectConversation = (threadId) => {
    setCurrentThread(threadId);
    setShowSidebar(false);
  };

  const renderUploadContent = () => {
    switch (uploadType) {
      case 'text':
        return (
          <div className="mb-4">
            <textarea
              value={uploadText}
              onChange={(e) => setUploadText(e.target.value)}
              placeholder="Paste your text here..."
              className="w-full p-2 border rounded mb-2"
            />
            <button
              onClick={handleUploadText}
              disabled={uploading || !uploadText.trim()}
              className="bg-blue-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
            >
              {uploading ? 'Uploading...' : 'Upload Text'}
            </button>
          </div>
        );
      case 'pdf':
        return (
          <div className="mb-4">
            <input
              type="file"
              onChange={(e) => setSelectedPdf(e.target.files[0])}
              accept=".pdf"
              className="mb-2"
            />
            <input
              type="number"
              value={startPage}
              onChange={(e) => setStartPage(Math.max(1, parseInt(e.target.value) || 1))}
              min="1"
              placeholder="Start Page"
              className="w-full p-2 border rounded mb-2"
            />
            <button
              onClick={handleUploadPdf}
              disabled={uploading || !selectedPdf}
              className="bg-green-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
            >
              {uploading ? 'Uploading...' : 'Upload PDF'}
            </button>
          </div>
        );
      case 'image':
        return (
          <div className="mb-4">
            <select
              value={imageType}
              onChange={(e) => setImageType(e.target.value)}
              className="w-full p-2 border rounded mb-2"
            >
              <option value="ocr">Text Image (OCR)</option>
              <option value="blip">Non-Text Image (Charts/Figures - BLIP Caption)</option>
            </select>
            <input
              type="file"
              onChange={(e) => setSelectedImage(e.target.files[0])}
              accept="image/*"
              className="mb-2"
            />
            <button
              onClick={handleUploadImage}
              disabled={uploading || !selectedImage}
              className="bg-green-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
            >
              {uploading ? 'Uploading...' : 'Upload Image'}
            </button>
          </div>
        );
      case 'csv':
        return (
          <div className="mb-4">
            <input
              type="file"
              onChange={(e) => setSelectedCsv(e.target.files[0])}
              accept=".csv"
              className="mb-2"
            />
            <button
              onClick={handleUploadCsv}
              disabled={uploading || !selectedCsv}
              className="bg-green-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
            >
              {uploading ? 'Uploading...' : 'Upload CSV'}
            </button>
          </div>
        );
      case 'excel':
        return (
          <div className="mb-4">
            <input
              type="file"
              onChange={(e) => setSelectedExcel(e.target.files[0])}
              accept=".xlsx,.xls"
              className="mb-2"
            />
            <button
              onClick={handleUploadExcel}
              disabled={uploading || !selectedExcel}
              className="bg-green-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
            >
              {uploading ? 'Uploading...' : 'Upload Excel'}
            </button>
          </div>
        );
      default:
        return null;
    }
  };

  if (loading) {
    return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  }

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
          <h2 className="text-2xl font-bold mb-6 text-center">HCP Chatbot</h2>
          {!showRegister ? (
            <>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Username"
                className="w-full p-2 border rounded mb-4"
              />
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Password"
                className="w-full p-2 border rounded mb-4"
              />
              <button
                onClick={handleLogin}
                className="w-full bg-blue-500 text-white py-2 rounded mb-4"
              >
                Login
              </button>
              <p className="text-center">
                Don't have an account?{' '}
                <span
                  onClick={() => setShowRegister(true)}
                  className="text-blue-500 cursor-pointer"
                >
                  Register
                </span>
              </p>
            </>
          ) : (
            <>
              <input
                type="text"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                placeholder="First Name"
                className="w-full p-2 border rounded mb-4"
              />
              <select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                className="w-full p-2 border rounded mb-4"
              >
                <option value="cadre">Cadre</option>
                <option value="admin">Admin</option>
              </select>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="Username"
                className="w-full p-2 border rounded mb-4"
              />
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Password"
                className="w-full p-2 border rounded mb-4"
              />
              <button
                onClick={handleRegister}
                className="w-full bg-green-500 text-white py-2 rounded mb-4"
              >
                Register
              </button>
              <p className="text-center">
                Already have an account?{' '}
                <span
                  onClick={() => setShowRegister(false)}
                  className="text-blue-500 cursor-pointer"
                >
                  Login
                </span>
              </p>
            </>
          )}
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-teal-50">
      <nav className="bg-white shadow-md p-4 flex justify-between items-center">
        <h1 className="text-2xl font-bold text-teal-600">HCP Chatbot</h1>
        <div className="flex items-center space-x-4">
          <span className="text-gray-700">
            {user ? `Welcome, ${user.first_name}!` : 'Welcome, Guest!'}
          </span>
          <button
            onClick={handleLogout}
            className="bg-red-500 text-white px-4 py-2 rounded"
          >
            Logout
          </button>
        </div>
      </nav>

      <div className="container mx-auto p-4 relative">
        <div className="flex">
          {/* Main Content */}
          <div className="flex-1">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="bg-white p-6 rounded-lg shadow">
                <h3 className="text-lg font-semibold mb-4">Upload Documents</h3>
                <button
                  onClick={() => setShowUploadModal(true)}
                  className="bg-blue-500 text-white px-4 py-2 rounded"
                >
                  Upload File
                </button>
              </div>

              <div className="bg-white p-6 rounded-lg shadow col-span-2 md:col-span-1">
                <h3 className="text-lg font-semibold mb-4">Ask Questions</h3>
                {currentThread && conversations[currentThread] && (
                  <div className="mb-4 max-h-64 overflow-y-auto">
                    {conversations[currentThread].map((chat, index) => (
                      <div key={index} className="mb-4">
                        <p className="font-semibold">Q: {chat.question}</p>
                        <p className="text-gray-700">A: {chat.answer}</p>
                      </div>
                    ))}
                  </div>
                )}
                <div className="flex items-center mb-4">
                  <input
                    type="text"
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    placeholder="Ask a question about your documents..."
                    onKeyPress={(e) => e.key === 'Enter' && !loading && handleAsk()}
                    className="flex-1 p-2 border rounded mr-2"
                  />
                  <button
                    onClick={handleAsk}
                    disabled={loading || !question.trim()}
                    className="bg-teal-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
                  >
                    {loading ? 'Thinking...' : 'Ask'}
                  </button>
                </div>
              </div>
            </div>
          </div>

          {/* Toggleable Sidebar */}
          {showSidebar && (
            <div className="fixed top-0 right-0 h-full w-64 bg-white shadow-lg p-4 overflow-y-auto z-50">
              <button
                onClick={() => setShowSidebar(false)}
                className="absolute top-2 right-2 text-gray-500"
              >
                Close
              </button>
              <h3 className="text-lg font-semibold mb-4">Conversation History</h3>
              {Object.keys(conversations).length > 0 ? (
                Object.keys(conversations).map((threadId) => (
                  <div
                    key={threadId}
                    onClick={() => selectConversation(threadId)}
                    className="cursor-pointer p-2 hover:bg-gray-100 rounded mb-2"
                  >
                    <p className="font-medium truncate">{conversations[threadId][0].question}</p>
                    <p className="text-sm text-gray-500">
                      {new Date(conversations[threadId][0].timestamp).toLocaleString()}
                    </p>
                  </div>
                ))
              ) : (
                <p className="text-gray-500">No conversations yet.</p>
              )}
              <button
                onClick={startNewConversation}
                className="bg-green-500 text-white px-4 py-2 rounded mt-4"
              >
                New Conversation
              </button>
            </div>
          )}
        </div>

        {/* Sidebar Toggle Button */}
        <button
          onClick={() => setShowSidebar(true)}
          className="fixed bottom-4 right-4 bg-teal-500 text-white p-3 rounded-full shadow-lg"
        >
          History
        </button>

        {/* Upload Modal */}
        {showUploadModal && (
          <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-white p-6 rounded-lg shadow-lg w-full max-w-md">
              <h3 className="text-lg font-semibold mb-4">Choose Upload Type</h3>
              {!uploadType ? (
                <div className="space-y-2">
                  <button onClick={() => setUploadType('text')} className="w-full bg-blue-500 text-white py-2 rounded">Upload Text</button>
                  <button onClick={() => setUploadType('pdf')} className="w-full bg-green-500 text-white py-2 rounded">Upload PDF</button>
                  <button onClick={() => setUploadType('image')} className="w-full bg-green-500 text-white py-2 rounded">Upload Image</button>
                  <button onClick={() => setUploadType('csv')} className="w-full bg-green-500 text-white py-2 rounded">Upload CSV</button>
                  <button onClick={() => setUploadType('excel')} className="w-full bg-green-500 text-white py-2 rounded">Upload Excel</button>
                </div>
              ) : (
                renderUploadContent()
              )}
              <button
                onClick={() => {
                  setShowUploadModal(false);
                  setUploadType(null);
                }}
                className="mt-4 text-red-500"
              >
                Cancel
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
