import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [role, setRole] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadText, setUploadText] = useState('');
  const [selectedPdf, setSelectedPdf] = useState(null); // New: For PDF file
  const [selectedImage, setSelectedImage] = useState(null); // New: For image file
  const [startPage, setStartPage] = useState(1);
  const [conversations, setConversations] = useState({});
  const [currentThread, setCurrentThread] = useState(null);

  const API_BASE = 'http://localhost:5000';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchConversations();
      setIsAuthenticated(true);
    }
  }, []);

  const fetchConversations = async () => {
    try {
      const res = await axios.get(`${API_BASE}/conversations`);
      console.log('Fetched conversations:', res.data); // Debug: Log to check if data is returned
      setConversations(res.data);
    } catch (error) {
      console.error('Error fetching conversations:', error);
    }
  };

  const handleRegister = async () => {
    try {
      const res = await axios.post(`${API_BASE}/register`, {
        first_name: firstName,
        role,
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setShowRegister(false);
      setFirstName('');
      setRole('');
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await axios.post(`${API_BASE}/login`, {
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setIsAuthenticated(false);
    setUser(null);
    setConversations({});
    setCurrentThread(null);
  };

  const handleUploadText = async () => {
    if (!uploadText.trim()) return alert('Text required');
    setUploading(true);
    try {
      await axios.post(`${API_BASE}/upload-text`, { text: uploadText });
      alert('Text uploaded');
      setUploadText('');
    } catch (error) {
      alert('Upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
    }
  };

  // New: Separate handler for PDF upload
  const handleUploadPdf = async () => {
    if (!selectedPdf) return alert('PDF file required');
    setUploading(true);
    const formData = new FormData();
    formData.append('file', selectedPdf);
    formData.append('startPage', startPage);
    try {
      await axios.post(`${API_BASE}/upload-file`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('PDF uploaded');
      setSelectedPdf(null);
      setStartPage(1);
    } catch (error) {
      alert('PDF upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
    }
  };

  // New: Separate handler for image upload (BLIP captioning happens in backend)
  const handleUploadImage = async () => {
    if (!selectedImage) return alert('Image file required');
    setUploading(true);
    const formData = new FormData();
    formData.append('file', selectedImage);
    try {
      await axios.post(`${API_BASE}/upload-file`, formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      alert('Image uploaded and captioned');
      setSelectedImage(null);
    } catch (error) {
      alert('Image upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
    }
  };

  const handleAsk = async () => {
    if (!question.trim()) return;
    setLoading(true);
    try {
      const res = await axios.post(`${API_BASE}/ask`, {
        question,
        thread_id: currentThread
      });
      setAnswer(res.data.answer);
      setCurrentThread(res.data.thread_id);
      fetchConversations();
    } catch (error) {
      setAnswer('Error: Failed to get answer - ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setLoading(false);
      setQuestion('');
    }
  };

  const startNewConversation = () => {
    setCurrentThread(null);
    setAnswer('');
  };

  return (
    <div className="min-h-screen bg-gray-100 flex items-center justify-center">
      {!isAuthenticated ? (
        <div className="bg-white p-8 rounded shadow-md w-96">
          <h2 className="text-2xl font-bold mb-4">{showRegister ? 'Register' : 'Login'}</h2>
          {showRegister && (
            <>
              <input
                type="text"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                placeholder="First Name"
                className="w-full p-2 mb-4 border rounded"
              />
              <input
                type="text"
                value={role}
                onChange={(e) => setRole(e.target.value)}
                placeholder="Role"
                className="w-full p-2 mb-4 border rounded"
              />
            </>
          )}
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
            className="w-full p-2 mb-4 border rounded"
          />
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            className="w-full p-2 mb-4 border rounded"
          />
          <button
            onClick={showRegister ? handleRegister : handleLogin}
            className="w-full bg-blue-500 text-white p-2 rounded"
          >
            {showRegister ? 'Register' : 'Login'}
          </button>
          <p className="mt-4 text-center">
            {showRegister ? 'Have an account?' : 'Need an account?'} <span onClick={() => setShowRegister(!showRegister)} className="text-blue-500 cursor-pointer">{showRegister ? 'Login' : 'Register'}</span>
          </p>
        </div>
      ) : (
        <div className="container mx-auto p-4">
          <div className="flex justify-between items-center mb-4">
            <h1 className="text-2xl font-bold">RAG Chatbot</h1>
            <button onClick={handleLogout} className="bg-red-500 text-white px-4 py-2 rounded">Logout</button>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="bg-white p-6 rounded-lg shadow">
              <h3 className="text-lg font-semibold mb-4">Conversation History</h3>
              {Object.keys(conversations).length === 0 ? (
                <p>No conversations yet.</p>
              ) : (
                Object.keys(conversations).map((thread) => (
                  <button
                    key={thread}
                    onClick={() => setCurrentThread(thread)}
                    className="block w-full text-left p-2 border-b"
                  >
                    {conversations[thread][0]?.question || thread.slice(0, 8)}
                  </button>
                ))
              )}
              <button onClick={startNewConversation} className="mt-4 bg-green-500 text-white px-4 py-2 rounded">New Conversation</button>
            </div>
            <div className="bg-white p-6 rounded-lg shadow">
              <h3 className="text-lg font-semibold mb-4">Upload Documents</h3>
              <div className="mb-4">
                <textarea
                  value={uploadText}
                  onChange={(e) => setUploadText(e.target.value)}
                  placeholder="Paste your text here..."
                  className="w-full p-2 border rounded mb-2"
                />
                <button
                  onClick={handleUploadText}
                  disabled={uploading || !uploadText.trim()}
                  className="bg-blue-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
                >
                  {uploading ? 'Uploading...' : 'Upload Text'}
                </button>
              </div>
              {/* New: Separate PDF Upload Section */}
              <div className="mb-4">
                <h4 className="text-md font-medium mb-2">Upload PDF</h4>
                <input
                  type="file"
                  onChange={(e) => setSelectedPdf(e.target.files[0])}
                  accept=".pdf"
                  className="mb-2"
                />
                <input
                  type="number"
                  value={startPage}
                  onChange={(e) => setStartPage(Math.max(1, parseInt(e.target.value) || 1))}
                  min="1"
                  placeholder="Start Page"
                  className="w-full p-2 border rounded mb-2"
                />
                <button
                  onClick={handleUploadPdf}
                  disabled={uploading || !selectedPdf}
                  className="bg-green-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
                >
                  {uploading ? 'Uploading...' : 'Upload PDF'}
                </button>
              </div>
              {/* New: Separate Image Upload Section */}
              <div>
                <h4 className="text-md font-medium mb-2">Upload Image</h4>
                <input
                  type="file"
                  onChange={(e) => setSelectedImage(e.target.files[0])}
                  accept="image/*"
                  className="mb-2"
                />
                <button
                  onClick={handleUploadImage}
                  disabled={uploading || !selectedImage}
                  className="bg-green-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
                >
                  {uploading ? 'Uploading...' : 'Upload Image'}
                </button>
              </div>
            </div>

            <div className="bg-white p-6 rounded-lg shadow col-span-2 md:col-span-2">
              <h3 className="text-lg font-semibold mb-4">Ask Questions</h3>
              {currentThread && conversations[currentThread] && (
                <div className="mb-4 max-h-64 overflow-y-auto">
                  {conversations[currentThread].map((chat, index) => (
                    <div key={index} className="mb-4">
                      <p className="font-semibold">Q: {chat.question}</p>
                      <p className="text-gray-700">A: {chat.answer}</p>
                    </div>
                  ))}
                </div>
              )}
              <div className="flex items-center mb-4">
                <input
                  type="text"
                  value={question}
                  onChange={(e) => setQuestion(e.target.value)}
                  placeholder="Ask a question about your documents..."
                  onKeyPress={(e) => e.key === 'Enter' && !loading && handleAsk()}
                  className="flex-1 p-2 border rounded mr-2"
                />
                <button
                  onClick={handleAsk}
                  disabled={loading || !question.trim()}
                  className="bg-teal-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
                >
                  {loading ? 'Thinking...' : 'Ask'}
                </button>
              </div>
              {answer && (
                <div className="p-4 bg-gray-50 rounded border">
                  <h4 className="font-semibold mb-2">Answer:</h4>
                  <p>{answer}</p>
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
