import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [role, setRole] = useState('cadre');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadText, setUploadText] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileType, setFileType] = useState('pdf');
  const [imageType, setImageType] = useState('ocr');
  const [startPage, setStartPage] = useState(1);
  const [conversations, setConversations] = useState({});
  const [currentThread, setCurrentThread] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  const API_BASE = 'http://localhost:5000';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchConversations();
      setIsAuthenticated(true);
    }
  }, []);

  const fetchConversations = async () => {
    try {
      const res = await axios.get(`${API_BASE}/conversations`);
      setConversations(res.data);
    } catch (error) {
      console.error('Error fetching conversations:', error);
    }
  };

  const handleRegister = async () => {
    try {
      const res = await axios.post(`${API_BASE}/register`, {
        first_name: firstName,
        role,
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setShowRegister(false);
      setFirstName('');
      setRole('cadre');
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await axios.post(`${API_BASE}/login`, {
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setIsAuthenticated(false);
    setUser(null);
    setConversations({});
    setCurrentThread(null);
    setIsSidebarOpen(false);
  };

  const handleUpload = async () => {
    if (fileType === 'text' && !uploadText.trim()) {
      return alert('Text required');
    }
    if (fileType !== 'text' && !selectedFile) {
      return alert('File required');
    }

    setUploading(true);
    const formData = new FormData();
    if (fileType !== 'text') {
      formData.append('file', selectedFile);
    }
    if (fileType === 'pdf') {
      formData.append('startPage', startPage);
    }
    if (fileType === 'image') {
      formData.append('imageType', imageType);
    }
    if (fileType === 'text') {
      formData.append('text', uploadText);
    }

    try {
      const endpoint = fileType === 'text' ? '/upload-text' : '/upload-file';
      await axios.post(`${API_BASE}${endpoint}`, fileType === 'text' ? { text: uploadText } : formData, {
        headers: fileType !== 'text' ? { 'Content-Type': 'multipart/form-data' } : {},
      });
      alert(`${fileType.charAt(0).toUpperCase() + fileType.slice(1)} uploaded`);
      setSelectedFile(null);
      setUploadText('');
      setStartPage(1);
      setImageType('ocr');
      setFileType('pdf');
    } catch (error) {
      alert('Upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
    }
  };

  const handleAsk = async () => {
    if (!question.trim()) return;
    setLoading(true);
    try {
      const res = await axios.post(`${API_BASE}/ask`, {
        question,
        thread_id: currentThread,
      });
      setAnswer(res.data.answer);
      setCurrentThread(res.data.thread_id);
      fetchConversations();
    } catch (error) {
      setAnswer('Error: Failed to get answer - ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setLoading(false);
      setQuestion('');
    }
  };

  const startNewConversation = () => {
    setCurrentThread(null);
    setAnswer('');
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {!isAuthenticated ? (
        <div className="flex items-center justify-center min-h-screen">
          <div className="bg-white p-8 rounded shadow-md w-96">
            <h2 className="text-2xl font-bold mb-4">{showRegister ? 'Register' : 'Login'}</h2>
            {showRegister && (
              <>
                <input
                  type="text"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                  placeholder="First Name"
                  className="w-full p-2 mb-4 border rounded"
                />
                <select
                  value={role}
                  onChange={(e) => setRole(e.target.value)}
                  className="w-full p-2 mb-4 border rounded"
                >
                  <option value="cadre">Cadre</option>
                  <option value="admin">Admin</option>
                </select>
              </>
            )}
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Username"
              className="w-full p-2 mb-4 border rounded"
            />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              className="w-full p-2 mb-4 border rounded"
            />
            <button
              onClick={showRegister ? handleRegister : handleLogin}
              className="w-full bg-blue-500 text-white p-2 rounded"
            >
              {showRegister ? 'Register' : 'Login'}
            </button>
            <p className="mt-4 text-center">
              {showRegister ? 'Have an account?' : 'Need an account?'}{' '}
              <span
                onClick={() => setShowRegister(!showRegister)}
                className="text-blue-500 cursor-pointer"
              >
                {showRegister ? 'Login' : 'Register'}
              </span>
            </p>
          </div>
        </div>
      ) : (
        <div className="relative flex min-h-screen">
          {/* Sidebar */}
          <div
            className={`fixed inset-y-0 left-0 w-64 bg-white shadow-lg transform ${
              isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
            } transition-transform duration-300 ease-in-out z-10`}
          >
            <div className="p-4">
              <h3 className="text-lg font-semibold mb-4">Conversation History</h3>
              {Object.keys(conversations).length === 0 ? (
                <p>No conversations yet.</p>
              ) : (
                Object.keys(conversations).map((thread) => (
                  <button
                    key={thread}
                    onClick={() => {
                      setCurrentThread(thread);
                      setIsSidebarOpen(false);
                    }}
                    className="block w-full text-left p-2 border-b hover:bg-gray-100"
                  >
                    {conversations[thread][0]?.question || thread.slice(0, 8)}
                  </button>
                ))
              )}
              <button
                onClick={() => {
                  startNewConversation();
                  setIsSidebarOpen(false);
                }}
                className="mt-4 bg-green-500 text-white px-4 py-2 rounded w-full"
              >
                New Conversation
              </button>
            </div>
          </div>

          {/* Main Content */}
          <div className="flex-1 p-4">
            <div className="container mx-auto">
              <div className="flex justify-between items-center mb-4">
                <div>
                  <h1 className="text-2xl font-bold">HCP Chatbot</h1>
                  <p className="text-gray-600">Hello, {user?.username || 'User'}!</p>
                </div>
                <div className="flex items-center space-x-4">
                  <button
                    onClick={() => setIsSidebarOpen(!isSidebarOpen)}
                    className="bg-blue-500 text-white px-4 py-2 rounded"
                  >
                    {isSidebarOpen ? 'Hide History' : 'Show History'}
                  </button>
                  <button
                    onClick={handleLogout}
                    className="bg-red-500 text-white px-4 py-2 rounded"
                  >
                    Logout
                  </button>
                </div>
              </div>

              <div className="grid grid-cols-1 gap-4">
                <div className="bg-white p-6 rounded-lg shadow">
                  <h3 className="text-lg font-semibold mb-4">Upload Document</h3>
                  <div className="mb-4">
                    <select
                      value={fileType}
                      onChange={(e) => setFileType(e.target.value)}
                      className="w-full p-2 border rounded mb-2"
                    >
                      <option value="pdf">PDF</option>
                      <option value="image">Image</option>
                      <option value="csv">CSV</option>
                      <option value="excel">Excel</option>
                      <option value="text">Text</option>
                    </select>

                    {fileType === 'text' && (
                      <textarea
                        value={uploadText}
                        onChange={(e) => setUploadText(e.target.value)}
                        placeholder="Paste your text here..."
                        className="w-full p-2 border rounded mb-2"
                      />
                    )}

                    {fileType === 'pdf' && (
                      <input
                        type="number"
                        value={startPage}
                        onChange={(e) => setStartPage(Math.max(1, parseInt(e.target.value) || 1))}
                        min="1"
                        placeholder="Start Page"
                        className="w-full p-2 border rounded mb-2"
                      />
                    )}

                    {fileType === 'image' && (
                      <select
                        value={imageType}
                        onChange={(e) => setImageType(e.target.value)}
                        className="w-full p-2 border rounded mb-2"
                      >
                        <option value="ocr">Text Image (OCR)</option>
                        <option value="blip">Non-Text Image (Charts/Figures - BLIP Caption)</option>
                      </select>
                    )}

                    {fileType !== 'text' && (
                      <input
                        type="file"
                        onChange={(e) => setSelectedFile(e.target.files[0])}
                        accept={
                          fileType === 'pdf'
                            ? '.pdf'
                            : fileType === 'image'
                            ? 'image/*'
                            : fileType === 'csv'
                            ? '.csv'
                            : '.xlsx,.xls'
                        }
                        className="mb-2"
                      />
                    )}

                    <button
                      onClick={handleUpload}
                      disabled={uploading || (fileType !== 'text' && !selectedFile) || (fileType === 'text' && !uploadText.trim())}
                      className="bg-green-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
                    >
                      {uploading ? 'Uploading...' : 'Upload Document'}
                    </button>
                  </div>
                </div>

                <div className="bg-white p-6 rounded-lg shadow">
                  <h3 className="text-lg font-semibold mb-4">Ask Questions</h3>
                  {currentThread && conversations[currentThread] && (
                    <div className="mb-4 max-h-64 overflow-y-auto">
                      {conversations[currentThread].map((chat, index) => (
                        <div key={index} className="mb-4">
                          <p className="font-semibold">Q: {chat.question}</p>
                          <p className="text-gray-700">A: {chat.answer}</p>
                          {chat.sources && chat.sources.length > 0 && (
                            <p className="text-sm text-gray-500">
                              Sources: {chat.sources.map((s) => s.file).join(', ')}
                            </p>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                  <div className="flex items-center mb-4">
                    <input
                      type="text"
                      value={question}
                      onChange={(e) => setQuestion(e.target.value)}
                      placeholder="Ask a question about your documents..."
                      onKeyPress={(e) => e.key === 'Enter' && !loading && handleAsk()}
                      className="flex-1 p-2 border rounded mr-2"
                    />
                    <button
                      onClick={handleAsk}
                      disabled={loading || !question.trim()}
                      className="bg-teal-500 text-white px-4 py-2 rounded disabled:bg-gray-400"
                    >
                      {loading ? 'Thinking...' : 'Ask'}
                    </button>
                  </div>
                  {answer && (
                    <div className="p-4 bg-gray-50 rounded border">
                      <h4 className="font-semibold mb-2">Answer:</h4>
                      <p>{answer}</p>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
