import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { ArrowLeftIcon, ArrowRightIcon, LogoutIcon } from '@heroicons/react/24/solid';

// Add UUID function at the top
function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [role, setRole] = useState('cadre');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadText, setUploadText] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileType, setFileType] = useState('pdf');
  const [imageType, setImageType] = useState('ocr');
  const [startPage, setStartPage] = useState(1);
  const [conversations, setConversations] = useState({});
  const [currentThread, setCurrentThread] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [showUploadModal, setShowUploadModal] = useState(false);
  // New state for RAG toggle
  const [isRagMode, setIsRagMode] = useState(true);
  // Add these to the state declarations
  const [tempImageBase64, setTempImageBase64] = useState(null);
  const [tempMimeType, setTempMimeType] = useState(null);
  // New states for temporary image upload in General Mode
  const [tempImageProcessing, setTempImageProcessing] = useState(false);
  const [showTempImageModal, setShowTempImageModal] = useState(false);
  const [tempImageFile, setTempImageFile] = useState(null);
  const [tempImageType, setTempImageType] = useState('ocr');
  const fileInputRef = useRef(null);

  const API_BASE = 'http://localhost:5000';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchConversations();
      setIsAuthenticated(true);
    }
  }, []);

  const fetchConversations = async () => {
    try {
      const res = await axios.get(`${API_BASE}/conversations`);
      setConversations(res.data);
    } catch (error) {
      console.error('Error fetching conversations:', error);
    }
  };

  const handleRegister = async () => {
    try {
      const res = await axios.post(`${API_BASE}/register`, {
        first_name: firstName,
        role,
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setShowRegister(false);
      setFirstName('');
      setRole('cadre');
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await axios.post(`${API_BASE}/login`, {
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setIsAuthenticated(false);
    setUser(null);
    setConversations({});
    setCurrentThread(null);
    setIsSidebarOpen(false);
  };

  const handleUpload = async () => {
    if (fileType === 'text' && !uploadText.trim()) {
      return alert('Text required');
    }
    if (fileType !== 'text' && !selectedFile) {
      return alert('File required');
    }

    setUploading(true);
    const formData = new FormData();
    if (fileType !== 'text') {
      formData.append('file', selectedFile);
    }
    if (fileType === 'pdf') {
      formData.append('startPage', startPage);
    }
    if (fileType === 'image') {
      formData.append('imageType', imageType);
    }
    if (fileType === 'text') {
      formData.append('text', uploadText);
    }

    try {
      const endpoint = fileType === 'text' ? '/upload-text' : '/upload-file';
      await axios.post(`${API_BASE}${endpoint}`, fileType === 'text' ? { text: uploadText } : formData, {
        headers: fileType !== 'text' ? { 'Content-Type': 'multipart/form-data' } : {},
      });
      alert(`${fileType.charAt(0).toUpperCase() + fileType.slice(1)} uploaded`);
      setSelectedFile(null);
      setUploadText('');
      setStartPage(1);
      setImageType('ocr');
      setFileType('pdf');
      setShowUploadModal(false);
    } catch (error) {
      alert('Upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
    }
  };

  const handleTempImageProcess = async () => {
    if (!tempImageFile) return;
    
    setTempImageProcessing(true);
    try {
      const reader = new FileReader();
      reader.onload = async () => {
        const fullDataUrl = reader.result;
        const base64 = fullDataUrl.split(',')[1]; // Base64 data without prefix
        
        if (tempImageType === 'ocr') {
          // Extract text using OCR
          try {
            const response = await axios.post(`${API_BASE}/process-temp-image-ocr`, {
              image: base64
            });
            const processedText = response.data.text || '';
            
            // Append OCR text to question
            setQuestion(prev => `${prev}\n\nExtracted text from image: ${processedText}`);
            alert('Text extracted from image and added to your question!');
          } catch (backendError) {
            console.warn('Backend OCR failed, trying direct API:', backendError);
            try {
              const EMBEDDINGS_API_BASE = 'http://localhost:8000';
              const response = await axios.post(`${EMBEDDINGS_API_BASE}/ocr`, {
                image: base64
              }, { timeout: 30000, headers: { 'Content-Type': 'application/json' } });
              const processedText = response.data.text || '';
              
              setQuestion(prev => `${prev}\n\nExtracted text from image: ${processedText}`);
              alert('Text extracted from image and added to your question!');
            } catch (directError) {
              console.error('Direct OCR API failed:', directError);
              alert('Failed to extract text from image: ' + (directError.response?.data?.error || directError.message));
            }
          }
        } else if (tempImageType === 'explain') {
          // Store image for AI analysis
          setTempImageBase64(base64);
          setTempMimeType(tempImageFile.type);
          alert('Image attached! Now ask a question about it â€” the AI will analyze the image along with your query.');
        }
        
        setShowTempImageModal(false);
        setTempImageFile(null);
      };
      reader.readAsDataURL(tempImageFile);
    } catch (error) {
      console.error('Image processing error:', error);
      alert('Failed to process image: ' + (error.response?.data?.error || error.message));
    } finally {
      setTempImageProcessing(false);
    }
  };

  const handleAsk = async () => {
  if (!question.trim()) return;
  
  setLoading(true);
  const threadId = currentThread || uuidv4();
  if (!currentThread) setCurrentThread(threadId);
  
  const newQA = { question, answer: '', sources: [] };
  setConversations(prev => ({
    ...prev,
    [threadId]: [...(prev[threadId] || []), newQA]
  }));
  
  try {
    const payload = {
      question,
      thread_id: threadId,
      force_general_mode: !isRagMode  // This is the key fix
    };
    
    // Add image if available (only for General mode)
    if (!isRagMode && tempImageBase64 && tempMimeType) {
      payload.image_base64 = tempImageBase64;
      payload.mime_type = tempMimeType;
    }
    
    const res = await axios.post(`${API_BASE}/ask`, payload);
    
    // FIX: Ensure thread exists and has items before accessing
    setConversations(prev => {
      const updatedConversations = { ...prev };
      const thread = updatedConversations[threadId];
      
      if (thread && thread.length > 0) {
        thread[thread.length - 1].answer = res.data.answer;
        thread[thread.length - 1].sources = res.data.sources || [];
      }
      
      return updatedConversations;
    });
    
    // Clear temp image after successful ask
    setTempImageBase64(null);
    setTempMimeType(null);
  } catch (error) {
    // FIX: Same safety check for error handling
    setConversations(prev => {
      const updatedConversations = { ...prev };
      const thread = updatedConversations[threadId];
      
      if (thread && thread.length > 0) {
        thread[thread.length - 1].answer = 'Error: ' + (error.response ? error.response.data.error : error.message);
      }
      
      return updatedConversations;
    });
  } finally {
    setLoading(false);
    setQuestion('');
  }
};

  const startNewConversation = () => {
    setCurrentThread(null);
  };

  const toggleRagMode = () => {
    setIsRagMode(!isRagMode);
    // Clear any attached image when switching modes
    setTempImageBase64(null);
    setTempMimeType(null);
  };

  const handleTempImageUpload = (event) => {
    const file = event.target.files[0];
    if (file && file.type.startsWith('image/')) {
      setTempImageFile(file);
      setShowTempImageModal(true);
    } else {
      alert('Please select a valid image file');
    }
    // Reset the file input
    event.target.value = '';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-100 via-purple-100 to-pink-100">
      {!isAuthenticated ? (
        <div className="flex items-center justify-center min-h-screen">
          <div className="bg-white p-8 rounded-lg shadow-xl w-96">
            <h2 className="text-4xl font-extrabold text-purple-600 mb-6 text-center">
              {showRegister ? 'Register' : 'Login'}
            </h2>
            {showRegister && (
              <>
                <input
                  type="text"
                  value={firstName}
                  onChange={(e) => setFirstName(e.target.value)}
                  placeholder="First Name"
                  className="w-full p-3 mb-4 border rounded-lg focus:ring-2 focus:ring-purple-500"
                />
                <select
                  value={role}
                  onChange={(e) => setRole(e.target.value)}
                  className="w-full p-3 mb-4 border rounded-lg focus:ring-2 focus:ring-purple-500"
                >
                  <option value="cadre">Cadre</option>
                  <option value="admin">Admin</option>
                </select>
              </>
            )}
            <input
              type="text"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Username"
              className="w-full p-3 mb-4 border rounded-lg focus:ring-2 focus:ring-purple-500"
            />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              className="w-full p-3 mb-4 border rounded-lg focus:ring-2 focus:ring-purple-500"
            />
            <button
              onClick={showRegister ? handleRegister : handleLogin}
              className="w-full bg-gradient-to-r from-purple-500 to-pink-500 text-white p-3 rounded-lg shadow-lg hover:shadow-xl transition-transform transform hover:scale-105"
            >
              {showRegister ? 'Register' : 'Login'}
            </button>
            <p className="mt-4 text-center text-gray-600">
              {showRegister ? 'Have an account?' : 'Need an account?'}{' '}
              <span
                onClick={() => setShowRegister(!showRegister)}
                className="text-purple-500 cursor-pointer hover:underline"
              >
                {showRegister ? 'Login' : 'Register'}
              </span>
            </p>
          </div>
        </div>
      ) : (
        <div className="relative flex min-h-screen">
          {/* Sidebar */}
          <div
            className={`fixed inset-y-0 left-0 w-64 bg-white shadow-lg transform ${
              isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
            } transition-transform duration-300 ease-in-out z-20`}
          >
            <div className="p-4">
              <h3 className="text-lg font-semibold text-purple-600 mb-4">Conversation History</h3>
              {Object.keys(conversations).length === 0 ? (
                <p className="text-gray-500">No conversations yet.</p>
              ) : (
                Object.keys(conversations).map((thread) => (
                  <button
                    key={thread}
                    onClick={() => {
                      setCurrentThread(thread);
                      setIsSidebarOpen(false);
                    }}
                    className="block w-full text-left p-3 border-b hover:bg-purple-100 rounded-lg transition-colors"
                  >
                    {conversations[thread][0]?.question.slice(0, 30) || thread.slice(0, 8)}
                  </button>
                ))
              )}
              <button
                onClick={() => {
                  startNewConversation();
                  setIsSidebarOpen(false);
                }}
                className="mt-4 bg-gradient-to-r from-green-400 to-blue-500 text-white px-4 py-2 rounded-lg shadow-lg hover:shadow-xl transition-transform transform hover:scale-105 w-full"
              >
                New Conversation
              </button>
            </div>
          </div>

          {/* Main Content */}
          <div className="flex-1 p-6">
            <div className="container mx-auto">
              <div className="flex justify-between items-center mb-6">
                <div className="flex items-center">
                  <button
                    onClick={() => setIsSidebarOpen(!isSidebarOpen)}
                    className="p-3 bg-purple-500 text-white rounded-full shadow-lg hover:bg-purple-600 transition-transform transform hover:scale-110"
                    title={isSidebarOpen ? 'Hide History' : 'Show History'}
                  >
                    {isSidebarOpen ? <ArrowLeftIcon className="w-6 h-6" /> : <ArrowRightIcon className="w-6 h-6" />}
                  </button>
                  <div className="ml-4">
                    <h1 className="text-4xl font-extrabold text-purple-700">HCP Chatbot</h1>
                    <p className="text-gray-600">Hello, {user?.username || 'User'}!</p>
                  </div>
                </div>
                <button
                  onClick={handleLogout}
                  className="flex items-center bg-red-500 text-white px-4 py-2 rounded-lg shadow-lg hover:bg-red-600 transition-transform transform hover:scale-105"
                >
                  <LogoutIcon className="w-5 h-5 mr-2" /> Logout
                </button>
              </div>

              {/* RAG Mode Toggle */}
              <div className="mb-6 bg-white p-6 rounded-lg shadow-lg">
                <div className="flex items-center justify-between">
                  <div>
                    <h4 className="font-semibold text-gray-700">Answer Mode</h4>
                    <p className="text-sm text-gray-500">
                      {isRagMode
                        ? "Document-based answers (RAG Mode): Answers will be based on your uploaded documents"
                        : "General conversation mode: Answers will be based on general knowledge like ChatGPT"}
                    </p>
                  </div>
                  <div className="flex items-center">
                    <span
                      className={`mr-3 text-sm font-medium ${!isRagMode ? 'text-purple-600' : 'text-gray-400'}`}
                    >
                      General
                    </span>
                    <label className="relative inline-flex items-center cursor-pointer">
                      <input
                        type="checkbox"
                        checked={isRagMode}
                        onChange={toggleRagMode}
                        className="sr-only peer"
                      />
                      <div className="w-11 h-6 bg-gray-200 peer-focus:outline-none peer-focus:ring-4 peer-focus:ring-purple-300 rounded-full peer peer-checked:after:translate-x-full peer-checked:after:border-white after:content-[''] after:absolute after:top-[2px] after:left-[2px] after:bg-white after:border-gray-300 after:border after:rounded-full after:h-5 after:w-5 after:transition-all peer-checked:bg-purple-600"></div>
                    </label>
                    <span
                      className={`ml-3 text-sm font-medium ${isRagMode ? 'text-purple-600' : 'text-gray-400'}`}
                    >
                      Documents
                    </span>
                  </div>
                </div>
              </div>

              <div className="bg-white p-6 rounded-lg shadow-lg min-h-[70vh] flex flex-col">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg font-semibold text-purple-600">Ask Questions</h3>
                  <div
                    className={`px-3 py-1 rounded-full text-xs font-medium ${
                      isRagMode
                        ? 'bg-purple-100 text-purple-800'
                        : 'bg-blue-100 text-blue-800'
                    }`}
                  >
                    {isRagMode ? 'Document Mode' : 'General Mode'}
                  </div>
                </div>
                
                {/* Show attached image indicator */}
                {!isRagMode && tempImageBase64 && (
                  <div className="mb-4 p-3 bg-green-50 border border-green-200 rounded-lg">
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-green-700">
                        ðŸ“· Image attached for AI analysis
                      </span>
                      <button
                        onClick={() => {
                          setTempImageBase64(null);
                          setTempMimeType(null);
                        }}
                        className="text-green-700 hover:text-green-900 text-sm font-medium"
                      >
                        Remove
                      </button>
                    </div>
                  </div>
                )}
                
                {/* Conversation History */}
                <div className="flex-1 mb-4 max-h-[60vh] overflow-y-auto">
                  {currentThread && conversations[currentThread] && (
                    <div className="space-y-4">
                      {conversations[currentThread].map((chat, index) => (
                        <div key={index} className="border-b pb-4 last:border-b-0">
                          <div className="mb-2">
                            <p className="font-semibold text-blue-600">Q: {chat.question}</p>
                          </div>
                          <div className="mb-2">
                            <p className="text-gray-800">A: {chat.answer}</p>
                          </div>
                          {chat.sources && chat.sources.length > 0 && (
                            <p className="text-sm text-gray-500">
                              Sources: {chat.sources.map((s) => `${s.file} (p. ${s.page})`).join(', ')}
                            </p>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                  
                  {(!currentThread || !conversations[currentThread]) && (
                    <div className="flex items-center justify-center h-full text-gray-500">
                      <p>Start a conversation by asking a question below...</p>
                    </div>
                  )}
                </div>

                {/* Question Input */}
                <div className="flex items-center gap-2 mt-auto">
                  {/* Image upload icon for General Mode only */}
                  {!isRagMode && (
                    <>
                      <input
                        ref={fileInputRef}
                        type="file"
                        accept="image/*"
                        onChange={handleTempImageUpload}
                        className="hidden"
                      />
                      <button
                        onClick={() => fileInputRef.current?.click()}
                        className="p-2 text-gray-500 hover:text-gray-700 hover:bg-gray-100 rounded-lg transition-colors"
                        title="Upload image for analysis"
                        disabled={loading || tempImageProcessing}
                      >
                        <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                          <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
                        </svg>
                      </button>
                    </>
                  )}
                  
                  <input
                    type="text"
                    value={question}
                    onChange={(e) => setQuestion(e.target.value)}
                    placeholder={
                      isRagMode 
                        ? "Ask a question about your documents..." 
                        : "Ask me anything (general conversation)..."
                    }
                    onKeyPress={(e) => e.key === 'Enter' && !loading && handleAsk()}
                    className="flex-1 p-3 border rounded-lg focus:outline-none focus:ring-2 focus:ring-purple-500"
                  />
                  <button
                    onClick={handleAsk}
                    disabled={loading || !question.trim()}
                    className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-6 py-3 rounded-lg shadow-lg disabled:bg-gray-400 disabled:cursor-not-allowed transition-transform transform hover:scale-105"
                  >
                    {loading ? 'Thinking...' : 'Ask'}
                  </button>
                </div>
              </div>
            </div>
          </div>

          {/* Floating Upload Button - Only show in RAG mode */}
          {isRagMode && (
            <button
              onClick={() => setShowUploadModal(true)}
              className="fixed bottom-4 right-4 bg-green-500 text-white p-4 rounded-full shadow-lg hover:bg-green-600 transition-colors"
              title="Upload Document"
            >
              +
            </button>
          )}

          {/* Upload Modal */}
          {showUploadModal && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-30">
              <div className="bg-white p-6 rounded-lg shadow-lg w-96">
                <h3 className="text-lg font-semibold mb-4">Upload Document</h3>
                <select
                  value={fileType}
                  onChange={(e) => setFileType(e.target.value)}
                  className="w-full p-2 border rounded mb-2"
                >
                  <option value="pdf">PDF</option>
                  <option value="image">Image</option>
                  <option value="csv">CSV</option>
                  <option value="excel">Excel</option>
                  <option value="text">Text</option>
                </select>

                {fileType === 'text' && (
                  <textarea
                    value={uploadText}
                    onChange={(e) => setUploadText(e.target.value)}
                    placeholder="Paste your text here..."
                    className="w-full p-2 border rounded mb-2 h-32 resize-vertical"
                  />
                )}

                {fileType === 'pdf' && (
                  <input
                    type="number"
                    value={startPage}
                    onChange={(e) => setStartPage(Math.max(1, parseInt(e.target.value) || 1))}
                    min="1"
                    placeholder="Start Page"
                    className="w-full p-2 border rounded mb-2"
                  />
                )}

                {fileType === 'image' && (
                  <select
                    value={imageType}
                    onChange={(e) => setImageType(e.target.value)}
                    className="w-full p-2 border rounded mb-2"
                  >
                    <option value="ocr">Text Image (OCR)</option>
                    <option value="blip">Non-Text Image (Charts/Figures - BLIP Caption)</option>
                  </select>
                )}

                {fileType !== 'text' && (
                  <input
                    type="file"
                    onChange={(e) => setSelectedFile(e.target.files[0])}
                    accept={
                      fileType === 'pdf'
                        ? '.pdf'
                        : fileType === 'image'
                        ? 'image/*'
                        : fileType === 'csv'
                        ? '.csv'
                        : '.xlsx,.xls'
                    }
                    className="mb-2 w-full"
                  />
                )}

                <div className="flex justify-end space-x-2">
                  <button
                    onClick={() => setShowUploadModal(false)}
                    className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={handleUpload}
                    disabled={uploading || (fileType !== 'text' && !selectedFile) || (fileType === 'text' && !uploadText.trim())}
                    className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
                  >
                    {uploading ? 'Uploading...' : 'Upload'}
                  </button>
                </div>
              </div>
            </div>
          )}

          {/* Temporary Image Processing Modal (General Mode) */}
          {showTempImageModal && (
            <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-30">
              <div className="bg-white p-6 rounded-lg shadow-lg w-96">
                <h3 className="text-lg font-semibold mb-4">Process Image</h3>
                <p className="text-sm text-gray-600 mb-4">
                  This image will be processed temporarily and not saved to the database.
                </p>
                
                {tempImageFile && (
                  <div className="mb-4">
                    <p className="text-sm font-medium text-gray-700 mb-2">Selected file:</p>
                    <p className="text-sm text-gray-600">{tempImageFile.name}</p>
                  </div>
                )}

                <select
                  value={tempImageType}
                  onChange={(e) => setTempImageType(e.target.value)}
                  className="w-full p-2 border rounded mb-4"
                >
                  <option value="ocr">Extract text (OCR)</option>
                  <option value="explain">Analyze image with AI</option>
                </select>

                <div className="text-sm text-gray-600 mb-4">
                  {tempImageType === 'ocr' 
                    ? "Text will be extracted from the image and added to your question."
                    : "The AI will analyze the image along with your question to provide a response."
                  }
                </div>

                <div className="flex justify-end space-x-2">
                  <button
                    onClick={() => {
                      setShowTempImageModal(false);
                      setTempImageFile(null);
                    }}
                    className="bg-gray-500 hover:bg-gray-600 text-white px-4 py-2 rounded transition-colors"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={handleTempImageProcess}
                    disabled={tempImageProcessing}
                    className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors"
                  >
                    {tempImageProcessing ? 'Processing...' : 'Process Image'}
                  </button>
                </div>
              </div>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
