import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [role, setRole] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [question, setQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadText, setUploadText] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [conversations, setConversations] = useState({});
  const [currentThread, setCurrentThread] = useState(null);

  const API_BASE = 'http://localhost:5000';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchConversations();
      setIsAuthenticated(true);
    }
  }, []);

  const fetchConversations = async () => {
    try {
      const res = await axios.get(`${API_BASE}/conversations`);
      setConversations(res.data);
    } catch (error) {
      console.error('Error fetching conversations:', error);
    }
  };

  const handleRegister = async () => {
    try {
      const res = await axios.post(`${API_BASE}/register`, {
        first_name: firstName,
        role,
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setShowRegister(false);
      setFirstName('');
      setRole('');
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Registration error:', error);
      alert('Error: ' + (error.response?.data?.error || 'Failed to register'));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await axios.post(`${API_BASE}/login`, { username, password });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Login error:', error);
      alert('Error: ' + (error.response?.data?.error || 'Failed to log in'));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setIsAuthenticated(false);
    setUser(null);
    setConversations({});
    setCurrentThread(null);
  };

  const handleAsk = async () => {
    if (!question.trim()) return;

    setLoading(true);
    try {
      const res = await axios.post(`${API_BASE}/ask`, {
        question,
        thread_id: currentThread,
      });
      setAnswer(res.data.answer);
      setConversations((prev) => {
        const newConversations = { ...prev };
        if (!newConversations[res.data.thread_id]) {
          newConversations[res.data.thread_id] = [];
        }
        newConversations[res.data.thread_id].push({ question, answer: res.data.answer });
        return newConversations;
      });
      setCurrentThread(res.data.thread_id);
      setQuestion('');
    } catch (error) {
      console.error('Error asking question:', error);
      setAnswer('Error: ' + (error.response?.data?.error || 'Failed to get answer'));
    }
    setLoading(false);
  };

  const handleUploadText = async () => {
    if (!uploadText.trim()) return;

    setUploading(true);
    try {
      const res = await axios.post(`${API_BASE}/upload-text`, { text: uploadText });
      alert(res.data.message);
      setUploadText('');
    } catch (error) {
      console.error('Error uploading text:', error);
      alert('Error: ' + (error.response?.data?.error || 'Failed to upload text'));
    }
    setUploading(false);
  };

  const handleUploadFile = async () => {
    if (!selectedFile) return;

    setUploading(true);
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const res = await axios.post(`${API_BASE}/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert(res.data.message);
      setSelectedFile(null);
      document.getElementById('file-input').value = '';
    } catch (error) {
      console.error('Error uploading file:', error);
      alert('Error: ' + (error.response?.data?.error || 'Failed to upload file'));
    }
    setUploading(false);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && (file.type === 'application/pdf' || file.type.startsWith('image/'))) {
      setSelectedFile(file);
    } else {
      alert('Please select a valid PDF or image file');
      e.target.value = '';
    }
  };

  const selectThread = (threadId) => {
    setCurrentThread(threadId);
    setAnswer('');
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
          <h2 className="text-2xl font-bold mb-6 text-center">
            {showRegister ? 'Register' : 'Login'}
          </h2>
          {showRegister && (
            <>
              <input
                type="text"
                placeholder="First Name"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                className="w-full p-2 mb-4 border rounded"
              />
              <input
                type="text"
                placeholder="Role"
                value={role}
                onChange={(e) => setRole(e.target.value)}
                className="w-full p-2 mb-4 border rounded"
              />
            </>
          )}
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="w-full p-2 mb-4 border rounded"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full p-2 mb-4 border rounded"
          />
          <button
            onClick={showRegister ? handleRegister : handleLogin}
            className="w-full bg-blue-500 text-white p-2 rounded hover:bg-blue-600"
          >
            {showRegister ? 'Register' : 'Login'}
          </button>
          <button
            onClick={() => setShowRegister(!showRegister)}
            className="w-full mt-4 text-blue-500 hover:underline"
          >
            {showRegister ? 'Already have an account? Login' : 'Need an account? Register'}
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <div className="max-w-4xl mx-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold text-gray-800">RAG Chatbot</h1>
          <div className="flex items-center">
            <span className="mr-4">Welcome, {user?.first_name} ({user?.role})</span>
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white px-4 py-2 rounded hover:bg-red-600"
            >
              Logout
            </button>
          </div>
        </div>

        <div className="flex">
          <div className="w-1/4 bg-white p-4 rounded-lg shadow mr-4">
            <h3 className="text-lg font-semibold mb-4">Conversation History</h3>
            {Object.keys(conversations).length === 0 ? (
              <p className="text-gray-500">No conversations yet.</p>
            ) : (
              <ul>
                {Object.keys(conversations).map((threadId) => (
                  <li
                    key={threadId}
                    onClick={() => selectThread(threadId)}
                    className={`p-2 cursor-pointer rounded hover:bg-gray-100 ${
                      currentThread === threadId ? 'bg-blue-100' : ''
                    }`}
                  >
                    Thread {threadId.slice(0, 8)}...
                  </li>
                ))}
              </ul>
            )}
            <button
              onClick={() => setCurrentThread(null)}
              className="mt-4 bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600"
            >
              New Conversation
            </button>
          </div>

          <div className="w-3/4">
            <div className="bg-white p-6 rounded-lg shadow mb-6">
              <h3 className="text-lg font-semibold mb-4">Upload Documents</h3>
              <div className="mb-4">
                <h4 className="mb-2">Upload Text:</h4>
                <textarea
                  value={uploadText}
                  onChange={(e) => setUploadText(e.target.value)}
                  placeholder="Paste your text here..."
                  className="w-full p-2 border rounded h-24"
                />
                <button
                  onClick={handleUploadText}
                  disabled={uploading || !uploadText.trim()}
                  className="mt-2 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 disabled:bg-gray-400"
                >
                  {uploading ? 'Uploading...' : 'Upload Text'}
                </button>
              </div>
              <div>
                <h4 className="mb-2">Upload PDF or Image:</h4>
                <input
                  id="file-input"
                  type="file"
                  accept=".pdf,image/*"
                  onChange={handleFileChange}
                  className="mb-2"
                />
                <button
                  onClick={handleUploadFile}
                  disabled={uploading || !selectedFile}
                  className="bg-green-500 text-white px-4 py-2 rounded hover:bg-green-600 disabled:bg-gray-400"
                >
                  {uploading ? 'Uploading...' : 'Upload File'}
                </button>
              </div>
            </div>

            <div className="bg-white p-6 rounded-lg shadow">
              <h3 className="text-lg font-semibold mb-4">Ask Questions</h3>
              {currentThread && conversations[currentThread] && (
                <div className="mb-4 max-h-64 overflow-y-auto">
                  {conversations[currentThread].map((chat, index) => (
                    <div key={index} className="mb-4">
                      <p className="font-semibold">Q: {chat.question}</p>
                      <p className="text-gray-700">A: {chat.answer}</p>
                    </div>
                  ))}
                </div>
              )}
              <div className="flex items-center mb-4">
                <input
                  type="text"
                  value={question}
                  onChange={(e) => setQuestion(e.target.value)}
                  placeholder="Ask a question about your documents..."
                  onKeyPress={(e) => e.key === 'Enter' && !loading && handleAsk()}
                  className="flex-1 p-2 border rounded mr-2"
                />
                <button
                  onClick={handleAsk}
                  disabled={loading || !question.trim()}
                  className="bg-teal-500 text-white px-4 py-2 rounded hover:bg-teal-600 disabled:bg-gray-400"
                >
                  {loading ? 'Thinking...' : 'Ask'}
                </button>
              </div>
              {answer && (
                <div className="p-4 bg-gray-50 rounded border">
                  <h4 className="font-semibold mb-2">Answer:</h4>
                  <p>{answer}</p>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
