import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadText, setUploadText] = useState("");
  const [selectedFile, setSelectedFile] = useState(null);

  const API_BASE = 'http://localhost:5000';

  const handleAsk = async () => {
    if (!question.trim()) return;
    
    setLoading(true);
    try {
      const res = await axios.post(`${API_BASE}/ask`, { question });
      setAnswer(res.data.answer);
    } catch (error) {
      console.error('Error asking question:', error);
      setAnswer('Error: ' + (error.response?.data?.error || 'Failed to get answer'));
    }
    setLoading(false);
  };

  const handleUploadText = async () => {
    if (!uploadText.trim()) return;
    
    setUploading(true);
    try {
      const res = await axios.post(`${API_BASE}/upload-text`, { text: uploadText });
      alert(res.data.message);
      setUploadText("");
    } catch (error) {
      console.error('Error uploading text:', error);
      alert('Error: ' + (error.response?.data?.error || 'Failed to upload text'));
    }
    setUploading(false);
  };

  const handleUploadFile = async () => {
    if (!selectedFile) return;
    
    setUploading(true);
    const formData = new FormData();
    formData.append('pdf', selectedFile);
    
    try {
      const res = await axios.post(`${API_BASE}/upload`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert(res.data.message);
      setSelectedFile(null);
      // Reset file input
      document.getElementById('file-input').value = '';
    } catch (error) {
      console.error('Error uploading file:', error);
      alert('Error: ' + (error.response?.data?.error || 'Failed to upload file'));
    }
    setUploading(false);
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && file.type === 'application/pdf') {
      setSelectedFile(file);
    } else {
      alert('Please select a valid PDF file');
      e.target.value = '';
    }
  };

  return (
    <div style={{ 
      padding: '20px', 
      maxWidth: '800px', 
      margin: '0 auto',
      fontFamily: 'Arial, sans-serif'
    }}>
      <h1 style={{ textAlign: 'center', color: '#333' }}>RAG Chatbot</h1>
      
      {/* Upload Section */}
      <div style={{ 
        marginBottom: '30px', 
        padding: '20px', 
        border: '1px solid #ddd', 
        borderRadius: '8px',
        backgroundColor: '#f9f9f9'
      }}>
        <h3>Upload Documents</h3>
        
        {/* Text Upload */}
        <div style={{ marginBottom: '20px' }}>
          <h4>Upload Text:</h4>
          <textarea
            value={uploadText}
            onChange={(e) => setUploadText(e.target.value)}
            placeholder="Paste your text here..."
            style={{ 
              width: '100%', 
              height: '100px', 
              marginBottom: '10px',
              padding: '8px',
              borderRadius: '4px',
              border: '1px solid #ccc'
            }}
          />
          <button 
            onClick={handleUploadText}
            disabled={uploading || !uploadText.trim()}
            style={{
              padding: '8px 16px',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            {uploading ? 'Uploading...' : 'Upload Text'}
          </button>
        </div>

        {/* File Upload */}
        <div>
          <h4>Upload PDF:</h4>
          <input
            id="file-input"
            type="file"
            accept=".pdf"
            onChange={handleFileChange}
            style={{ marginBottom: '10px' }}
          />
          <br />
          <button 
            onClick={handleUploadFile}
            disabled={uploading || !selectedFile}
            style={{
              padding: '8px 16px',
              backgroundColor: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            {uploading ? 'Uploading...' : 'Upload PDF'}
          </button>
        </div>
      </div>

      {/* Chat Section */}
      <div style={{ 
        padding: '20px', 
        border: '1px solid #ddd', 
        borderRadius: '8px',
        backgroundColor: '#fff'
      }}>
        <h3>Ask Questions</h3>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: '20px' }}>
          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            placeholder="Ask a question about your documents..."
            onKeyPress={(e) => e.key === 'Enter' && !loading && handleAsk()}
            style={{ 
              flex: 1,
              padding: '10px',
              marginRight: '10px',
              borderRadius: '4px',
              border: '1px solid #ccc',
              fontSize: '16px'
            }}
          />
          <button 
            onClick={handleAsk}
            disabled={loading || !question.trim()}
            style={{
              padding: '10px 20px',
              backgroundColor: '#17a2b8',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '16px'
            }}
          >
            {loading ? 'Thinking...' : 'Ask'}
          </button>
        </div>

        {answer && (
          <div style={{ 
            marginTop: '20px',
            padding: '15px',
            backgroundColor: '#f8f9fa',
            borderRadius: '4px',
            border: '1px solid #e9ecef'
          }}>
            <h4 style={{ margin: '0 0 10px 0', color: '#495057' }}>Answer:</h4>
            <p style={{ margin: '0', lineHeight: '1.5' }}>{answer}</p>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
