import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import { 
  LoginIcon, 
  UserIcon, 
  UploadIcon, 
  SendIcon, 
  ImageIcon, 
  MenuIcon, 
  XIcon,
  ChevronLeftIcon,
  ChevronRightIcon
} from '@heroicons/react/solid'; // Assuming Heroicons are installed; npm install @heroicons/react

// Add UUID function at the top
function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    var r = Math.random() * 16 | 0, v = c == 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [showRegister, setShowRegister] = useState(false);
  const [firstName, setFirstName] = useState('');
  const [role, setRole] = useState('cadre');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [question, setQuestion] = useState('');
  const [loading, setLoading] = useState(false);
  const [uploading, setUploading] = useState(false);
  const [uploadText, setUploadText] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [fileType, setFileType] = useState('pdf');
  const [imageType, setImageType] = useState('ocr');
  const [startPage, setStartPage] = useState(1);
  const [conversations, setConversations] = useState({});
  const [currentThread, setCurrentThread] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);
  const [showUploadModal, setShowUploadModal] = useState(false);
  // New state for RAG toggle
  const [isRagMode, setIsRagMode] = useState(true);
  // Add these to the state declarations
  const [tempImageBase64, setTempImageBase64] = useState(null);
  const [tempMimeType, setTempMimeType] = useState(null);
  // New states for temporary image upload in General Mode
  const [tempImageProcessing, setTempImageProcessing] = useState(false);
  const [showTempImageModal, setShowTempImageModal] = useState(false);
  const [tempImageFile, setTempImageFile] = useState(null);
  const [tempImageType, setTempImageType] = useState('ocr');
  const fileInputRef = useRef(null);

  const API_BASE = 'http://localhost:5000';

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      fetchConversations();
      setIsAuthenticated(true);
    }
  }, []);

  const fetchConversations = async () => {
    try {
      const res = await axios.get(`${API_BASE}/conversations`);
      setConversations(res.data);
    } catch (error) {
      console.error('Error fetching conversations:', error);
    }
  };

  const handleRegister = async () => {
    try {
      const res = await axios.post(`${API_BASE}/register`, {
        first_name: firstName,
        role,
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setShowRegister(false);
      setFirstName('');
      setRole('cadre');
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Registration error:', error);
      alert('Registration failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogin = async () => {
    try {
      const res = await axios.post(`${API_BASE}/login`, {
        username,
        password,
      });
      localStorage.setItem('token', res.data.token);
      axios.defaults.headers.common['Authorization'] = `Bearer ${res.data.token}`;
      setUser(res.data.user);
      setIsAuthenticated(true);
      setUsername('');
      setPassword('');
      fetchConversations();
    } catch (error) {
      console.error('Login error:', error);
      alert('Login failed: ' + (error.response ? error.response.data.error : error.message));
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    delete axios.defaults.headers.common['Authorization'];
    setIsAuthenticated(false);
    setUser(null);
    setConversations({});
    setCurrentThread(null);
    setIsSidebarOpen(false);
  };

  const handleUpload = async () => {
    if (fileType === 'text' && !uploadText.trim()) {
      return alert('Text required');
    }
    if (fileType !== 'text' && !selectedFile) {
      return alert('File required');
    }

    setUploading(true);
    const formData = new FormData();
    if (fileType !== 'text') {
      formData.append('file', selectedFile);
    }
    if (fileType === 'pdf') {
      formData.append('startPage', startPage);
    }
    if (fileType === 'image') {
      formData.append('imageType', imageType);
    }
    if (fileType === 'text') {
      formData.append('text', uploadText);
    }

    try {
      const endpoint = fileType === 'text' ? '/upload-text' : '/upload-file';
      await axios.post(`${API_BASE}${endpoint}`, fileType === 'text' ? { text: uploadText } : formData, {
        headers: fileType !== 'text' ? { 'Content-Type': 'multipart/form-data' } : {},
      });
      alert(`${fileType.charAt(0).toUpperCase() + fileType.slice(1)} uploaded`);
      setSelectedFile(null);
      setUploadText('');
      setStartPage(1);
      setImageType('ocr');
      setFileType('pdf');
      setShowUploadModal(false);
    } catch (error) {
      alert('Upload failed: ' + (error.response ? error.response.data.error : error.message));
    } finally {
      setUploading(false);
    }
  };

  const handleTempImageProcess = async () => {
    if (!tempImageFile) return;
    
    setTempImageProcessing(true);
    try {
      const reader = new FileReader();
      reader.onload = async () => {
        const fullDataUrl = reader.result;
        const base64 = fullDataUrl.split(',')[1]; // Base64 data without prefix
        
        if (tempImageType === 'ocr') {
          // Extract text using OCR
          try {
            const response = await axios.post(`${API_BASE}/process-temp-image-ocr`, {
              image: base64
            });
            const processedText = response.data.text || '';
            
            // Append OCR text to question
            setQuestion(prev => `${prev}\n\nExtracted text from image: ${processedText}`);
            alert('Text extracted from image and added to your question!');
          } catch (backendError) {
            console.warn('Backend OCR failed, trying direct API:', backendError);
            try {
              const EMBEDDINGS_API_BASE = 'http://localhost:8000';
              const response = await axios.post(`${EMBEDDINGS_API_BASE}/ocr`, {
                image: base64
              }, { timeout: 30000, headers: { 'Content-Type': 'application/json' } });
              const processedText = response.data.text || '';
              
              setQuestion(prev => `${prev}\n\nExtracted text from image: ${processedText}`);
              alert('Text extracted from image and added to your question!');
            } catch (directError) {
              console.error('Direct OCR API failed:', directError);
              alert('Failed to extract text from image: ' + (directError.response?.data?.error || directError.message));
            }
          }
        } else if (tempImageType === 'explain') {
          // Store image for AI analysis
          setTempImageBase64(base64);
          setTempMimeType(tempImageFile.type);
          alert('Image attached! Now ask a question about it â€” the AI will analyze the image along with your query.');
        }
        
        setShowTempImageModal(false);
        setTempImageFile(null);
      };
      reader.readAsDataURL(tempImageFile);
    } catch (error) {
      console.error('Image processing error:', error);
      alert('Failed to process image: ' + (error.response?.data?.error || error.message));
    } finally {
      setTempImageProcessing(false);
    }
  };

  const handleAsk = async () => {
    if (!question.trim()) return;
    setLoading(true);
    const thread_id = uuidv4();
    try {
      const response = await axios.post(`${API_BASE}/ask`, {
        question,
        rag_mode: isRagMode,
        temp_image: tempImageBase64 ? { base64: tempImageBase64, mime_type: tempMimeType } : null
      });
      // Handle response (append to chat history, etc.)
      console.log('Response:', response.data);
      // Clear temp image after use
      setTempImageBase64(null);
      setTempMimeType(null);
    } catch (error) {
      console.error('Ask error:', error);
    } finally {
      setLoading(false);
      setQuestion('');
    }
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 flex items-center justify-center px-4">
        <div className="max-w-md w-full space-y-8 bg-white/10 backdrop-blur-lg rounded-2xl shadow-2xl p-8 border border-white/20">
          <div className="text-center">
            <div className="mx-auto h-16 w-16 bg-gradient-to-r from-teal-400 to-blue-500 rounded-full flex items-center justify-center mb-4">
              <LoginIcon className="h-8 w-8 text-white" />
            </div>
            <h2 className="text-3xl font-bold text-white mb-2">Welcome Back</h2>
            <p className="text-gray-300">Sign in to your account</p>
          </div>
          <div className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-200 mb-2">Username</label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-xl text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all"
                placeholder="Enter your username"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-200 mb-2">Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-xl text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all"
                placeholder="Enter your password"
              />
            </div>
            <button
              onClick={handleLogin}
              className="w-full bg-gradient-to-r from-teal-500 to-blue-600 text-white py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl hover:from-teal-600 hover:to-blue-700 transform hover:scale-105 transition-all duration-200 flex items-center justify-center space-x-2"
            >
              <LoginIcon className="h-5 w-5" />
              <span>Sign In</span>
            </button>
            <div className="text-center">
              <button
                onClick={() => setShowRegister(true)}
                className="text-teal-400 hover:text-teal-300 font-medium transition-colors"
              >
                Don't have an account? Register
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (showRegister) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-purple-900 via-blue-900 to-indigo-900 flex items-center justify-center px-4">
        <div className="max-w-md w-full space-y-8 bg-white/10 backdrop-blur-lg rounded-2xl shadow-2xl p-8 border border-white/20">
          <div className="text-center">
            <div className="mx-auto h-16 w-16 bg-gradient-to-r from-green-400 to-teal-500 rounded-full flex items-center justify-center mb-4">
              <UserIcon className="h-8 w-8 text-white" />
            </div>
            <h2 className="text-3xl font-bold text-white mb-2">Create Account</h2>
            <p className="text-gray-300">Join the conversation</p>
          </div>
          <div className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-200 mb-2">First Name</label>
              <input
                type="text"
                value={firstName}
                onChange={(e) => setFirstName(e.target.value)}
                className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-xl text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all"
                placeholder="Enter your first name"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-200 mb-2">Role</label>
              <select
                value={role}
                onChange={(e) => setRole(e.target.value)}
                className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-xl text-white focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all"
              >
                <option value="cadre">Cadre</option>
                <option value="admin">Admin</option>
                {/* Add more roles as needed */}
              </select>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-200 mb-2">Username</label>
              <input
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-xl text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all"
                placeholder="Choose a username"
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-200 mb-2">Password</label>
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-xl text-white placeholder-gray-300 focus:outline-none focus:ring-2 focus:ring-teal-500 focus:border-transparent transition-all"
                placeholder="Create a password"
              />
            </div>
            <button
              onClick={handleRegister}
              className="w-full bg-gradient-to-r from-green-500 to-teal-600 text-white py-3 rounded-xl font-semibold shadow-lg hover:shadow-xl hover:from-green-600 hover:to-teal-700 transform hover:scale-105 transition-all duration-200 flex items-center justify-center space-x-2"
            >
              <UserIcon className="h-5 w-5" />
              <span>Create Account</span>
            </button>
            <div className="text-center">
              <button
                onClick={() => setShowRegister(false)}
                className="text-teal-400 hover:text-teal-300 font-medium transition-colors"
              >
                Already have an account? Sign In
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Main Chat Interface
  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-50 to-gray-100 overflow-hidden">
      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-20 w-80 bg-white shadow-xl transform ${isSidebarOpen ? 'translate-x-0' : '-translate-x-full'} transition-transform duration-300 ease-in-out md:relative md:translate-x-0 md:z-auto`}>
        <div className="flex items-center justify-between p-4 border-b border-gray-200">
          <h2 className="text-xl font-bold text-gray-800">Conversations</h2>
          <button
            onClick={() => setIsSidebarOpen(false)}
            className="md:hidden text-gray-500 hover:text-gray-700 p-1 rounded-full hover:bg-gray-100 transition-colors"
          >
            <XIcon className="h-5 w-5" />
          </button>
        </div>
        <div className="p-4 space-y-2 max-h-full overflow-y-auto">
          {Object.entries(conversations).map(([threadId, messages]) => (
            <div
              key={threadId}
              onClick={() => setCurrentThread(threadId)}
              className={`p-4 rounded-xl cursor-pointer transition-all duration-200 ${
                currentThread === threadId
                  ? 'bg-gradient-to-r from-teal-500 to-blue-500 text-white shadow-md'
                  : 'bg-gray-50 hover:bg-gray-100 text-gray-700'
              }`}
            >
              <p className="font-medium truncate">{messages[0]?.question || 'New Conversation'}</p>
              <p className="text-xs opacity-75 mt-1 truncate">{messages[0]?.answer?.substring(0, 50)}...</p>
              <span className="text-xs opacity-50 block mt-2">{new Date(messages[0]?.timestamp).toLocaleDateString()}</span>
            </div>
          ))}
          {Object.keys(conversations).length === 0 && (
            <div className="text-center text-gray-500 py-8">
              <p className="text-sm">No conversations yet</p>
              <p className="text-xs">Start chatting to see history here</p>
            </div>
          )}
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Header */}
        <header className="bg-white shadow-sm border-b border-gray-200 px-6 py-4 flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => setIsSidebarOpen(!isSidebarOpen)}
              className="md:hidden p-2 rounded-lg hover:bg-gray-100 transition-colors"
            >
              <MenuIcon className="h-6 w-6 text-gray-600" />
            </button>
            <h1 className="text-2xl font-bold bg-gradient-to-r from-teal-500 to-blue-500 bg-clip-text text-transparent">
              {isRagMode ? 'RAG Chatbot' : 'General AI'}
            </h1>
          </div>
          <div className="flex items-center space-x-4">
            <button
              onClick={() => setIsRagMode(!isRagMode)}
              className={`px-4 py-2 rounded-lg font-medium transition-all duration-200 ${
                isRagMode
                  ? 'bg-teal-500 text-white shadow-md hover:shadow-lg'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              {isRagMode ? 'RAG Mode' : 'General Mode'}
            </button>
            <button
              onClick={handleLogout}
              className="text-gray-600 hover:text-gray-800 p-2 rounded-full hover:bg-gray-100 transition-colors"
            >
              <XIcon className="h-6 w-6" />
            </button>
          </div>
        </header>

        {/* Chat Messages Area */}
        <div className="flex-1 overflow-y-auto p-6 space-y-6">
          {/* Example message bubbles - replace with actual messages */}
          {currentThread && conversations[currentThread]?.map((msg, idx) => (
            <div key={idx} className="space-y-2">
              <div className="flex justify-start">
                <div className="max-w-xs lg:max-w-md px-4 py-2 bg-blue-500 text-white rounded-2xl rounded-br-md shadow-md">
                  <p className="text-sm">{msg.question}</p>
                </div>
              </div>
              <div className="flex justify-end">
                <div className="max-w-xs lg:max-w-md px-4 py-2 bg-gray-200 text-gray-800 rounded-2xl rounded-bl-md shadow-md">
                  <p className="text-sm">{msg.answer}</p>
                </div>
              </div>
            </div>
          ))}
          {loading && (
            <div className="flex justify-end">
              <div className="inline-flex items-center space-x-2 px-4 py-2 bg-gray-100 rounded-2xl rounded-bl-md shadow-md">
                <div className="animate-pulse">Thinking...</div>
              </div>
            </div>
          )}
          {!currentThread && (
            <div className="text-center py-12 text-gray-500">
              <div className="mx-auto h-24 w-24 bg-gradient-to-r from-teal-400 to-blue-500 rounded-full flex items-center justify-center mb-4">
                <SendIcon className="h-12 w-12 text-white" />
              </div>
              <h3 className="text-xl font-semibold mb-2">Start a Conversation</h3>
              <p className="text-sm">Click on a conversation or start a new one below</p>
            </div>
          )}
        </div>

        {/* Input Area */}
        <div className="bg-white border-t border-gray-200 p-6">
          <div className="max-w-4xl mx-auto">
            <div className="relative">
              {/* Temp Image Upload Button for General Mode */}
              {!isRagMode && (
                <label className="absolute left-0 top-1/2 transform -translate-y-1/2 z-10">
                  <input
                    type="file"
                    accept="image/*"
                    onChange={(e) => {
                      setTempImageFile(e.target.files[0]);
                      setShowTempImageModal(true);
                    }}
                    className="hidden"
                  />
                  <button className="p-2 bg-gray-200 hover:bg-gray-300 rounded-full transition-colors shadow-md">
                    <ImageIcon className="h-5 w-5 text-gray-600" />
                  </button>
                </label>
              )}
              <input
                type="text"
                value={question}
                onChange={(e) => setQuestion(e.target.value)}
                placeholder={
                  isRagMode 
                    ? "Ask a question about your documents..." 
                    : "Ask me anything (general conversation)..."
                }
                onKeyPress={(e) => e.key === 'Enter' && !loading && handleAsk()}
                className="w-full pl-12 pr-16 py-4 border-2 border-gray-200 rounded-2xl focus:outline-none focus:border-teal-500 focus:ring-4 focus:ring-teal-500/10 transition-all duration-200 text-lg placeholder-gray-500"
              />
              <button
                onClick={handleAsk}
                disabled={loading || !question.trim()}
                className="absolute right-1 top-1/2 transform -translate-y-1/2 bg-gradient-to-r from-teal-500 to-blue-600 text-white p-3 rounded-xl shadow-lg hover:shadow-xl hover:from-teal-600 hover:to-blue-700 disabled:opacity-50 disabled:cursor-not-allowed transform hover:scale-105 transition-all duration-200"
              >
                <SendIcon className="h-5 w-5" />
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Floating Upload Button - Only show in RAG mode */}
      {isRagMode && (
        <button
          onClick={() => setShowUploadModal(true)}
          className="fixed bottom-6 right-6 bg-gradient-to-r from-green-500 to-teal-600 text-white p-4 rounded-full shadow-xl hover:shadow-2xl hover:from-green-600 hover:to-teal-700 transform hover:scale-110 transition-all duration-200 z-10"
          title="Upload Document"
        >
          <UploadIcon className="h-6 w-6" />
        </button>
      )}

      {/* Upload Modal */}
      {showUploadModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-30 p-4">
          <div className="bg-white rounded-2xl shadow-2xl w-full max-w-md overflow-hidden animate-in slide-in-from-bottom-4 duration-300">
            <div className="bg-gradient-to-r from-green-500 to-teal-600 p-6 text-white">
              <h3 className="text-xl font-bold flex items-center space-x-2">
                <UploadIcon className="h-6 w-6" />
                <span>Upload Document</span>
              </h3>
            </div>
            <div className="p-6 space-y-4">
              <select
                value={fileType}
                onChange={(e) => setFileType(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 transition-all"
              >
                <option value="pdf">PDF Document</option>
                <option value="image">Image File</option>
                <option value="csv">CSV Data</option>
                <option value="excel">Excel Sheet</option>
                <option value="text">Plain Text</option>
              </select>

              {fileType === 'text' && (
                <textarea
                  value={uploadText}
                  onChange={(e) => setUploadText(e.target.value)}
                  placeholder="Paste your text content here..."
                  className="w-full p-3 border border-gray-300 rounded-xl h-32 resize-none focus:outline-none focus:ring-2 focus:ring-green-500 transition-all"
                />
              )}

              {fileType === 'pdf' && (
                <input
                  type="number"
                  value={startPage}
                  onChange={(e) => setStartPage(Math.max(1, parseInt(e.target.value) || 1))}
                  min="1"
                  placeholder="Start from page"
                  className="w-full p-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 transition-all"
                />
              )}

              {fileType === 'image' && (
                <select
                  value={imageType}
                  onChange={(e) => setImageType(e.target.value)}
                  className="w-full p-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 transition-all"
                >
                  <option value="ocr">Text-Heavy Image (OCR)</option>
                  <option value="blip">Visual Content (AI Caption)</option>
                </select>
              )}

              {fileType !== 'text' && (
                <input
                  type="file"
                  onChange={(e) => setSelectedFile(e.target.files[0])}
                  accept={
                    fileType === 'pdf'
                      ? '.pdf'
                      : fileType === 'image'
                      ? 'image/*'
                      : fileType === 'csv'
                      ? '.csv'
                      : '.xlsx,.xls'
                  }
                  className="w-full p-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-green-500 transition-all file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:text-sm file:font-semibold file:bg-green-50 file:text-green-700 hover:file:bg-green-100"
                />
              )}

              <div className="flex space-x-3 pt-4">
                <button
                  onClick={() => setShowUploadModal(false)}
                  className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-700 py-3 rounded-xl font-medium transition-all duration-200"
                >
                  Cancel
                </button>
                <button
                  onClick={handleUpload}
                  disabled={uploading || (fileType !== 'text' && !selectedFile) || (fileType === 'text' && !uploadText.trim())}
                  className="flex-1 bg-gradient-to-r from-green-500 to-teal-600 text-white py-3 rounded-xl font-medium shadow-md hover:shadow-lg disabled:opacity-50 disabled:cursor-not-allowed transform hover:scale-105 transition-all duration-200 flex items-center justify-center space-x-2"
                >
                  {uploading ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                      <span>Uploading...</span>
                    </>
                  ) : (
                    <>
                      <UploadIcon className="h-4 w-4" />
                      <span>Upload</span>
                    </>
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Temporary Image Processing Modal (General Mode) */}
      {showTempImageModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-30 p-4">
          <div className="bg-white rounded-2xl shadow-2xl w-full max-w-md overflow-hidden animate-in slide-in-from-bottom-4 duration-300">
            <div className="bg-gradient-to-r from-blue-500 to-indigo-600 p-6 text-white">
              <h3 className="text-xl font-bold flex items-center space-x-2">
                <ImageIcon className="h-6 w-6" />
                <span>Process Image</span>
              </h3>
            </div>
            <div className="p-6 space-y-4">
              <p className="text-sm text-gray-600">
                This image will be analyzed on-the-fly without saving to your library.
              </p>
              
              {tempImageFile && (
                <div className="p-4 bg-gray-50 rounded-xl">
                  <p className="text-sm font-medium text-gray-700 mb-1">Selected:</p>
                  <p className="text-sm text-gray-600 truncate">{tempImageFile.name}</p>
                </div>
              )}

              <select
                value={tempImageType}
                onChange={(e) => setTempImageType(e.target.value)}
                className="w-full p-3 border border-gray-300 rounded-xl focus:outline-none focus:ring-2 focus:ring-blue-500 transition-all"
              >
                <option value="ocr">Extract Text (OCR)</option>
                <option value="explain">AI Image Analysis</option>
              </select>

              <div className="text-sm text-gray-600 p-3 bg-blue-50 rounded-xl">
                {tempImageType === 'ocr' 
                  ? "Pulls readable text from the image and adds it to your query."
                  : "AI will describe and analyze the image in context with your question."
                }
              </div>

              <div className="flex space-x-3 pt-4">
                <button
                  onClick={() => {
                    setShowTempImageModal(false);
                    setTempImageFile(null);
                  }}
                  className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-700 py-3 rounded-xl font-medium transition-all duration-200"
                >
                  Cancel
                </button>
                <button
                  onClick={handleTempImageProcess}
                  disabled={tempImageProcessing}
                  className="flex-1 bg-gradient-to-r from-blue-500 to-indigo-600 text-white py-3 rounded-xl font-medium shadow-md hover:shadow-lg disabled:opacity-50 disabled:cursor-not-allowed transform hover:scale-105 transition-all duration-200 flex items-center justify-center space-x-2"
                >
                  {tempImageProcessing ? (
                    <>
                      <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                      <span>Processing...</span>
                    </>
                  ) : (
                    <>
                      <ImageIcon className="h-4 w-4" />
                      <span>Analyze</span>
                    </>
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
